/**
 * Stores original network-related methods to restore them later
 */
const originalMethods = {
  fetch: typeof window !== 'undefined' ? window.fetch : null,
  XMLHttpRequest:
    typeof XMLHttpRequest !== 'undefined'
      ? {
          open: XMLHttpRequest.prototype.open,
          send: XMLHttpRequest.prototype.send,
        }
      : null,
  setTimeout: setTimeout,
  WebSocket: typeof WebSocket !== 'undefined' ? WebSocket : null,
};

/**
 * Network degradation simulation state
 */
interface SimulationState {
  enabled: boolean;
  currentCondition: NetworkCondition | null;
  proxiedFetch: boolean;
  proxiedXHR: boolean;
  proxiedWebSocket: boolean;
}

/**
 * Current state of the network degradation simulator
 */
const simulationState: SimulationState = {
  enabled: false,
  currentCondition: null,
  proxiedFetch: false,
  proxiedXHR: false,
  proxiedWebSocket: false,
};

/**
 * Calculate actual delay based on network condition parameters
 * @param condition The network condition
 */
function calculateDelay(condition: NetworkCondition): number {
  if (!condition) return 0;

  // Base latency
  let delay = condition.latencyMs;

  // Add jitter (random variance in latency)
  if (condition.jitterMs > 0) {
    delay += (Math.random() * 2 - 1) * condition.jitterMs;
  }

  // Ensure delay is never negative
  return Math.max(0, delay);
}

/**
 * Simulate connection stall if enabled in the network condition
 * @param condition The network condition
 */
function simulateConnectionStall(condition: NetworkCondition): Promise<void> {
  if (
    !condition.enableConnectionStalls ||
    typeof condition.stallProbability !== 'number' ||
    typeof condition.stallDurationMs !== 'number'
  ) {
    return Promise.resolve();
  }

  // Determine if a stall should occur
  if (Math.random() < condition.stallProbability) {
    return new Promise(resolve => {
      setTimeout(resolve, condition.stallDurationMs);
    });
  }

  return Promise.resolve();
}

/**
 * Simulate packet loss based on the network condition
 * @param condition The network condition
 * @throws Error if packet is "lost"
 */
function simulatePacketLoss(condition: NetworkCondition): void {
  if (Math.random() < condition.packetLoss) {
    throw new Error(`Network error: Simulated packet loss (${condition.name})`);
  }
}

/**
 * Calculate delay for a data transfer based on throughput
 * @param byteSize Size of data in bytes
 * @param condition The network condition
 */
function calculateThroughputDelay(byteSize: number, condition: NetworkCondition): number {
  if (!condition || condition.throughputKbps <= 0) return 0;

  // Convert bytes to bits and calculate transfer time in seconds
  const bits = byteSize * 8;
  const seconds = bits / (condition.throughputKbps * 1000);

  // Convert to milliseconds
  return seconds * 1000;
}

/**
 * Apply network degradation to fetch API
 * @param condition The network condition to apply
 */
function proxyFetch(condition: NetworkCondition): void {
  if (typeof window === 'undefined' || !window.fetch || simulationState.proxiedFetch) return;

  // Store original fetch
  const originalFetch = window.fetch;

  // Override fetch with degraded version
  window.fetch = async (input: RequestInfo | URL, init?: RequestInit) => {
    if (!simulationState.enabled) {
      return originalFetch(input, init);
    }

    try {
      // Simulate initial latency (request)
      const latency = calculateDelay(condition);
      await new Promise(resolve => setTimeout(resolve, latency));

      // Simulate connection stalls
      await simulateConnectionStall(condition);

      // Simulate packet loss
      simulatePacketLoss(condition);

      // Make the actual request
      const response = await originalFetch(input, init);

      // Clone the response to access its body
      const clone = response.clone();
      const text = await clone.text();

      // Calculate throughput delay based on response size
      const byteSize = new TextEncoder().encode(text).length;
      const throughputDelay = calculateThroughputDelay(byteSize, condition);

      // Simulate response latency + throughput delay
      await new Promise(resolve => setTimeout(resolve, latency + throughputDelay));

      // Simulate packet loss again (for response)
      simulatePacketLoss(condition);

      // Create a new response with the same data
      return new Response(text, {
        status: response.status,
        statusText: response.statusText,
        headers: response.headers,
      });
    } catch (error) {
      if (error.message.includes('Simulated packet loss')) {
        throw new TypeError('NetworkError when attempting to fetch resource');
      }
      throw error;
    }
  };

  simulationState.proxiedFetch = true;
}

/**
 * Apply network degradation to XMLHttpRequest
 * @param condition The network condition to apply
 */
function proxyXHR(condition: NetworkCondition): void {
  if (typeof XMLHttpRequest === 'undefined' || simulationState.proxiedXHR) return;

  // Store original XHR methods
  const originalOpen = XMLHttpRequest.prototype.open;
  const originalSend = XMLHttpRequest.prototype.send;

  // Override XHR open method
  XMLHttpRequest.prototype.open = function (this: XMLHttpRequest, ...args: any[]) {
    // Store network condition in the XHR instance
    (this as any).__networkCondition = simulationState.enabled ? condition : null;
    return originalOpen.apply(this, args);
  };

  // Override XHR send method
  XMLHttpRequest.prototype.send = function (this: XMLHttpRequest, ...args: any[]) {
    const xhrNetworkCondition = (this as any).__networkCondition;

    if (!xhrNetworkCondition) {
      return originalSend.apply(this, args);
    }

    // Calculate request latency
    const latency = calculateDelay(xhrNetworkCondition);

    // Store original callbacks
    const originalOnload = this.onload;
    const originalOnerror = this.onerror;
    const originalOnreadystatechange = this.onreadystatechange;

    // Simulate packet loss
    if (Math.random() < xhrNetworkCondition.packetLoss) {
      setTimeout(() => {
        if (typeof originalOnerror === 'function') {
          const errorEvent = new ErrorEvent('error', {
            message: 'Simulated packet loss',
          });
          originalOnerror.call(this, errorEvent);
        }
      }, latency);

      return;
    }

    // Handle readystatechange
    this.onreadystatechange = function (this: XMLHttpRequest, ...rsArgs: any[]) {
      if (this.readyState === 4) {
        // Calculate throughput delay for the response
        let throughputDelay = 0;
        if (this.responseText) {
          const byteSize = new TextEncoder().encode(this.responseText).length;
          throughputDelay = calculateThroughputDelay(byteSize, xhrNetworkCondition);
        }

        // Delay the readystatechange with both latency and throughput delay
        setTimeout(() => {
          if (typeof originalOnreadystatechange === 'function') {
            originalOnreadystatechange.apply(this, rsArgs);
          }
        }, latency + throughputDelay);

        return;
      }

      if (typeof originalOnreadystatechange === 'function') {
        originalOnreadystatechange.apply(this, rsArgs);
      }
    };

    // Delay the actual send
    setTimeout(() => {
      originalSend.apply(this, args);
    }, latency);
  };

  simulationState.proxiedXHR = true;
}

/**
 * Apply network degradation to WebSocket
 * @param condition The network condition to apply
 */
function proxyWebSocket(condition: NetworkCondition): void {
  if (typeof WebSocket === 'undefined' || simulationState.proxiedWebSocket) return;

  // Store original WebSocket constructor
  const OriginalWebSocket = WebSocket;

  // Create a proxy WebSocket class
  class DegradedWebSocket extends OriginalWebSocket {
    constructor(...args: any[]) {
      super(...args);

      if (!simulationState.enabled) return;

      // Store original event handlers
      const originalOnopen = this.onopen;
      const originalOnmessage = this.onmessage;
      const originalOnclose = this.onclose;
      const originalOnerror = this.onerror;

      // Apply connection latency for open event
      this.onopen = function (this: WebSocket, ev: Event) {
        setTimeout(() => {
          if (typeof originalOnopen === 'function') {
            originalOnopen.call(this, ev);
          }
        }, calculateDelay(condition));
      };

      // Apply latency and packet loss to messages
      this.onmessage = function (this: WebSocket, ev: MessageEvent) {
        // Simulate packet loss
        if (Math.random() < condition.packetLoss) {
          return; // Message lost
        }

        // Calculate message throughput delay
        let throughputDelay = 0;
        if (typeof ev.data === 'string') {
          const byteSize = new TextEncoder().encode(ev.data).length;
          throughputDelay = calculateThroughputDelay(byteSize, condition);
        } else if (ev.data instanceof Blob) {
          throughputDelay = calculateThroughputDelay(ev.data.size, condition);
        } else if (ev.data instanceof ArrayBuffer) {
          throughputDelay = calculateThroughputDelay(ev.data.byteLength, condition);
        }

        // Delay message by latency + throughput
        setTimeout(
          () => {
            if (typeof originalOnmessage === 'function') {
              originalOnmessage.call(this, ev);
            }
          },
          calculateDelay(condition) + throughputDelay
        );
      };

      // Store other callbacks
      this.onclose = originalOnclose;
      this.onerror = originalOnerror;

      // Override send method to add latency and packet loss
      const originalSend = this.send;
      this.send = function (
        this: WebSocket,
        data: string | ArrayBufferLike | Blob | ArrayBufferView
      ) {
        // Simulate packet loss for outgoing messages
        if (Math.random() < condition.packetLoss) {
          // Trigger error for lost packet
          if (typeof this.onerror === 'function') {
            const errorEvent = new ErrorEvent('error', {
              message: 'Simulated packet loss for outgoing message',
            });
            this.onerror.call(this, errorEvent as Event);
          }
          return;
        }

        // Calculate latency
        setTimeout(() => {
          try {
            originalSend.call(this, data);
          } catch (e) {
            if (typeof this.onerror === 'function') {
              this.onerror.call(this, new Event('error'));
            }
          }
        }, calculateDelay(condition));
      };
    }
  }

  // Replace global WebSocket with our degraded version
  window.WebSocket = DegradedWebSocket as any;

  simulationState.proxiedWebSocket = true;
}

/**
 * Interface for options when enabling network degradation
 */
export interface EnableOptions {
  /** Whether to proxy fetch API */
  proxyFetch?: boolean;

  /** Whether to proxy XMLHttpRequest */
  proxyXHR?: boolean;

  /** Whether to proxy WebSocket */
  proxyWebSocket?: boolean;
}

/**
 * Enable network degradation simulation with specified conditions
 * @param condition The network condition to simulate
 * @param options Options for enabling network degradation
 */
export function enableNetworkDegradation(
  condition: NetworkCondition,
  options: EnableOptions = { proxyFetch: true, proxyXHR: true, proxyWebSocket: true }
): void {
  // Already enabled with same condition - do nothing
  if (simulationState.enabled && simulationState.currentCondition?.name === condition.name) {
    return;
  }

  // If previously enabled with different condition, disable first
  if (simulationState.enabled) {
    disableNetworkDegradation();
  }

  // Update simulation state
  simulationState.enabled = true;
  simulationState.currentCondition = condition;

  // Apply proxies based on options
  if (options.proxyFetch !== false) {
    proxyFetch(condition);
  }

  if (options.proxyXHR !== false) {
    proxyXHR(condition);
  }

  if (options.proxyWebSocket !== false) {
    proxyWebSocket(condition);
  }

  console.log(`Network degradation simulation enabled: ${condition.name}`);
  console.log(
    `  Latency: ${condition.latencyMs}ms, Throughput: ${condition.throughputKbps}Kbps, Packet Loss: ${condition.packetLoss * 100}%`
  );
}

/**
 * Disable network degradation simulation and restore original behavior
 */
export function disableNetworkDegradation(): void {
  if (!simulationState.enabled) return;

  // Restore original methods
  if (simulationState.proxiedFetch && typeof window !== 'undefined' && originalMethods.fetch) {
    window.fetch = originalMethods.fetch;
    simulationState.proxiedFetch = false;
  }

  if (
    simulationState.proxiedXHR &&
    typeof XMLHttpRequest !== 'undefined' &&
    originalMethods.XMLHttpRequest
  ) {
    XMLHttpRequest.prototype.open = originalMethods.XMLHttpRequest.open;
    XMLHttpRequest.prototype.send = originalMethods.XMLHttpRequest.send;
    simulationState.proxiedXHR = false;
  }

  if (
    simulationState.proxiedWebSocket &&
    typeof window !== 'undefined' &&
    originalMethods.WebSocket
  ) {
    window.WebSocket = originalMethods.WebSocket;
    simulationState.proxiedWebSocket = false;
  }

  // Reset simulation state
  simulationState.enabled = false;
  simulationState.currentCondition = null;

  console.log('Network degradation simulation disabled');
}

/**
 * Get the current network condition being simulated
 * @returns The current network condition or null if disabled
 */
export function getCurrentNetworkCondition(): NetworkCondition | null {
  return simulationState.enabled ? simulationState.currentCondition : null;
}

/**
 * Check if network degradation simulation is enabled
 * @returns True if enabled, false otherwise
 */
export function isNetworkDegradationEnabled(): boolean {
  return simulationState.enabled;
}

/**
 * Create a custom network condition
 * @param config Configuration for the custom network condition
 * @returns A new NetworkCondition object
 */
export function createCustomNetworkCondition(
  config: Partial<NetworkCondition> & { name: string }
): NetworkCondition {
  return {
    description: config.description || 'Custom network condition',
    latencyMs: config.latencyMs || 0,
    throughputKbps: config.throughputKbps || 1000000,
    packetLoss: config.packetLoss || 0,
    jitterMs: config.jitterMs || 0,
    enableConnectionStalls: config.enableConnectionStalls || false,
    stallDurationMs: config.stallDurationMs,
    stallProbability: config.stallProbability,
    ...config,
  };
}

/**
 * Apply network degradation to a specific function or Promise
 * Useful for targeted testing without affecting the entire application
 *
 * @param condition The network condition to simulate
 * @param fn The function to degrade
 * @returns A new function with network degradation applied
 */
export function withNetworkDegradation<T extends (...args: any[]) => any>(
  condition: NetworkCondition,
  fn: T
): (...args: Parameters<T>) => Promise<ReturnType<T>> {
  return async (...args: Parameters<T>): Promise<ReturnType<T>> => {
    // Initial request latency
    const latency = calculateDelay(condition);
    await new Promise(resolve => setTimeout(resolve, latency));

    // Simulate connection stall
    await simulateConnectionStall(condition);

    // Simulate packet loss
    if (Math.random() < condition.packetLoss) {
      throw new Error(`Network error: Simulated packet loss (${condition.name})`);
    }

    // Call the original function
    const result = await fn(...args);

    // Calculate response size for throughput
    let byteSize = 1000; // Default size estimate

    if (result && typeof result === 'object') {
      try {
        const resultString = JSON.stringify(result);
        byteSize = new TextEncoder().encode(resultString).length;
      } catch (e) {
        // Ignore, use default size
      }
    } else if (typeof result === 'string') {
      byteSize = new TextEncoder().encode(result).length;
    }

    // Calculate throughput delay
    const throughputDelay = calculateThroughputDelay(byteSize, condition);

    // Response latency
    await new Promise(resolve => setTimeout(resolve, latency + throughputDelay));

    // Response packet loss
    if (Math.random() < condition.packetLoss) {
      throw new Error(`Network error: Simulated packet loss (${condition.name})`);
    }

    return result;
  };
}

/**
 * Run a test under simulated network conditions
 * @param condition The network condition to simulate
 * @param testFn The test function to run
 * @param options Options for enabling network degradation
 * @returns The result of the test function
 */
export async function runWithNetworkCondition<T>(
  condition: NetworkCondition,
  testFn: () => Promise<T>,
  options?: EnableOptions
): Promise<T> {
  try {
    // Enable network degradation
    enableNetworkDegradation(condition, options);

    // Run the test
    return await testFn();
  } finally {
    // Always disable network degradation when done
    disableNetworkDegradation();
  }
}

/**
 * Run the same test across multiple network conditions
 * @param testFn The test function to run
 * @param conditions The network conditions to test with
 * @param options Options for enabling network degradation
 * @returns Array of results for each network condition
 */
export async function runAcrossNetworkConditions<T>(
  testFn: (condition: NetworkCondition) => Promise<T>,
  conditions: NetworkCondition[] = Object.values(NetworkProfiles),
  options?: EnableOptions
): Promise<Array<{ condition: NetworkCondition; result: T }>> {
  const results: Array<{ condition: NetworkCondition; result: T }> = [];

  for (const condition of conditions) {
    try {
      // Enable network degradation for this condition
      enableNetworkDegradation(condition, options);

      // Run the test
      const result = await testFn(condition);

      // Store result
      results.push({ condition, result });
    } catch (error) {
      // Store error as result
      results.push({
        condition,
        result: { error, message: error.message } as unknown as T,
      });
    } finally {
      // Disable network degradation between tests
      disableNetworkDegradation();
    }
  }

  return results;
}

/**
 * @file ServiceAccess.ts
 * Utilities for safely accessing game services/managers
 *
 * This file provides:
 * 1. Type-safe access to global services
 * 2. Error handling for missing services
 * 3. Registration utilities for services
 * 4. Service availability monitoring
 */

/**
 * Error thrown when a required service is not available
 */
export class ServiceNotAvailableError extends Error {
  constructor(serviceName: string) {
    super(`Service '${serviceName}' is not available. Ensure it is initialized before access.`);
    this.name = 'ServiceNotAvailableError';
  }
}

/**
 * Type for service validation functions
 */
type ServiceValidator<T> = (service: T | undefined) => boolean;

/**
 * Default validator just checks if the service exists
 */
const defaultValidator = <T>(service: T | undefined): boolean => service !== undefined;

/**
 * Safely get a service from the window object with type checking
 *
 * @param serviceName The name of the service property on window
 * @param validator Optional function to validate the service (beyond just checking existence)
 * @param throwIfMissing Whether to throw an error if the service is missing
 * @returns The service instance with proper typing
 */
export function getService<T>(
  serviceName: keyof Window & string,
  validator: ServiceValidator<T> = defaultValidator,
  throwIfMissing = false
): T | undefined {
  const service = window[serviceName] as unknown as T;

  if (!validator(service)) {
    if (throwIfMissing) {
      throw new ServiceNotAvailableError(serviceName);
    }
    return undefined;
  }

  return service;
}

/**
 * Get a service that is required by the application
 * Throws an error if the service is not available
 *
 * @param serviceName The name of the service property on window
 * @param validator Optional function to validate the service
 * @returns The service instance with proper typing (never undefined)
 * @throws ServiceNotAvailableError if service is not available
 */
export function getRequiredService<T>(
  serviceName: keyof Window & string,
  validator: ServiceValidator<T> = defaultValidator
): T {
  const service = getService<T>(serviceName, validator, true);
  // TypeScript doesn't know that getService with throwIfMissing=true will never return undefined
  return service as T;
}

/**
 * Register a service on the window object with type safety
 *
 * @param serviceName Name of the service to register
 * @param serviceInstance The service instance to register
 */
export function registerService<T>(serviceName: keyof Window & string, serviceInstance: T): void {
  // Convert to unknown first as suggested by the linter
  (window as unknown as Record<string, unknown>)[serviceName] = serviceInstance;
  console.log(`Service '${serviceName}' registered successfully.`);
}

/**
 * Check if multiple services are available
 *
 * @param serviceNames Array of service names to check
 * @returns Object with results for each service
 */
export function checkServicesAvailability(
  serviceNames: Array<keyof Window & string>
): Record<string, boolean> {
  return serviceNames.reduce(
    (result, name) => {
      result[name] = getService(name) !== undefined;
      return result;
    },
    {} as Record<string, boolean>
  );
}

/**
 * Wait for a service to become available
 *
 * @param serviceName Name of the service to wait for
 * @param timeout Maximum time to wait in milliseconds (default: 5000ms)
 * @param interval Check interval in milliseconds (default: 100ms)
 * @returns Promise that resolves with the service or rejects on timeout
 */
export function waitForService<T>(
  serviceName: keyof Window & string,
  timeout = 5000,
  interval = 100
): Promise<T> {
  return new Promise<T>((resolve, reject) => {
    const startTime = Date.now();

    const checkService = () => {
      const service = window[serviceName] as unknown as T;

      if (service) {
        resolve(service);
        return;
      }

      const elapsedTime = Date.now() - startTime;
      if (elapsedTime >= timeout) {
        reject(new ServiceNotAvailableError(serviceName));
        return;
      }

      setTimeout(checkService, interval);
    };

    checkService();
  });
}

/**
 * Represents a migration function that transforms state from one version to another
 */
export type MigrationFn<T = unknown> = (state: unknown) => T;

/**
 * Represents a migration step with a target version and migration function
 */
interface MigrationStep<T = unknown> {
  /**
   * The version this migration transforms the state to
   */
  toVersion: number;

  /**
   * The migration function
   */
  migrate: MigrationFn<T>;
}

/**
 * Options for creating a migration manager
 */
interface MigrationManagerOptions<T> {
  /**
   * The current version of the state schema
   */
  currentVersion: number;

  /**
   * Initial migrations to register
   */
  migrations?: MigrationStep<T>[];

  /**
   * Optional function to validate the final migrated state
   */
  validate?: (state: T) => boolean;

  /**
   * Optional flag to enable debug logging
   */
  debug?: boolean;
}

/**
 * Creates a migration manager that handles state schema migrations
 *
 * @param options Configuration options for the migration manager
 * @returns An object with methods to register migrations and migrate state
 */
export function createMigrationManager<T>(options: MigrationManagerOptions<T>) {
  const { currentVersion, migrations: initialMigrations = [], validate, debug = false } = options;

  // Store migrations in a map for quick lookup by version
  const migrations = new Map<number, MigrationStep<T>>();

  // Register initial migrations
  initialMigrations.forEach(migration => {
    migrations.set(migration.toVersion, migration);
  });

  const log = (message: string, ...args: unknown[]) => {
    if (debug) {
      console.log(`[MigrationManager] ${message}`, ...args);
    }
  };

  /**
   * Registers a new migration step
   *
   * @param toVersion The version this migration transforms the state to
   * @param migrateFn The migration function
   * @returns The migration manager for chaining
   */
  const registerMigration = (toVersion: number, migrateFn: MigrationFn<T>) => {
    if (toVersion > currentVersion) {
      throw new Error(
        `Cannot register migration to version ${toVersion} because it is greater than the current version ${currentVersion}`
      );
    }

    if (migrations.has(toVersion)) {
      log(`Overriding existing migration for version ${toVersion}`);
    }

    migrations.set(toVersion, { toVersion, migrate: migrateFn });
    log(`Registered migration to version ${toVersion}`);

    return { registerMigration, migrateState };
  };

  /**
   * Migrates state from a previous version to the current version
   *
   * @param state The state to migrate
   * @param fromVersion The version of the state
   * @returns The migrated state
   * @throws Error if migration fails
   */
  const migrateState = (state: unknown, fromVersion: number): T => {
    if (fromVersion === currentVersion) {
      log('State is already at current version, no migration needed');
      return state as T;
    }

    if (fromVersion > currentVersion) {
      throw new Error(
        `Cannot migrate from version ${fromVersion} because it is greater than the current version ${currentVersion}`
      );
    }

    log(`Starting migration from version ${fromVersion} to ${currentVersion}`);

    // Get all migrations that need to be applied in order
    const migrationsToApply = Array.from(migrations.values())
      .filter(
        migration => migration.toVersion > fromVersion && migration.toVersion <= currentVersion
      )
      .sort((a, b) => a.toVersion - b.toVersion);

    if (migrationsToApply.length === 0) {
      log('No migrations found, returning state as-is');
      return state as T;
    }

    log(`Found ${migrationsToApply.length} migrations to apply`);

    // Apply migrations in sequence
    let migratedState = state;
    for (const migration of migrationsToApply) {
      try {
        log(`Applying migration to version ${migration.toVersion}`);
        migratedState = migration.migrate(migratedState);
      } catch (error) {
        const errorMessage = `Failed to migrate to version ${migration.toVersion}: ${error instanceof Error ? error.message : String(error)}`;
        log(errorMessage);
        throw new Error(errorMessage);
      }
    }

    // Validate the final state if a validator is provided
    if (validate && !validate(migratedState as T)) {
      const errorMessage = 'Validation failed after migration';
      log(errorMessage);
      throw new Error(errorMessage);
    }

    log('Migration completed successfully');
    return migratedState as T;
  };

  return {
    registerMigration,
    migrateState,
  };
}

/**
 * Creates a migration builder to help define migrations in a fluent API
 *
 * @param currentVersion The current version of the state schema
 * @returns A builder object to define migrations
 */
export function createMigrationBuilder<T>(currentVersion: number) {
  const migrations: MigrationStep<T>[] = [];

  /**
   * Adds a migration step
   *
   * @param toVersion The version this migration transforms the state to
   * @param migrateFn The migration function
   * @returns The builder for chaining
   */
  const addMigration = (toVersion: number, migrateFn: MigrationFn<T>) => {
    migrations.push({ toVersion, migrate: migrateFn });
    return { addMigration, build };
  };

  /**
   * Builds the migration manager
   *
   * @param options Additional options for the migration manager
   * @returns The migration manager
   */
  const build = (
    options: Omit<MigrationManagerOptions<T>, 'currentVersion' | 'migrations'> = {}
  ) => {
    return createMigrationManager({
      currentVersion,
      migrations,
      ...options,
    });
  };

  return {
    addMigration,
    build,
  };
}

/**
 * Helper function to add a property to a state object with a default value
 *
 * @param state The state object
 * @param key The property key
 * @param defaultValue The default value
 * @returns A new state object with the property added
 */
export function addProperty<T extends Record<string, unknown>, K extends string, V>(
  state: T,
  key: K,
  defaultValue: V
): T & Record<K, V> {
  return {
    ...state,
    [key]: defaultValue,
  };
}

/**
 * Helper function to rename a property in a state object
 *
 * @param state The state object
 * @param oldKey The old property key
 * @param newKey The new property key
 * @returns A new state object with the property renamed
 */
export function renameProperty<T extends Record<string, unknown>, K extends string>(
  state: T,
  oldKey: keyof T,
  newKey: K
): Omit<T, typeof oldKey> & Record<K, unknown> {
  const { [oldKey]: value, ...rest } = state;
  return {
    ...rest,
    [newKey]: value,
  } as Omit<T, typeof oldKey> & Record<K, unknown>;
}

/**
 * Helper function to remove a property from a state object
 *
 * @param state The state object
 * @param key The property key to remove
 * @returns A new state object with the property removed
 */
export function removeProperty<T extends Record<string, unknown>, K extends keyof T>(
  state: T,
  key: K
): Omit<T, K> {
  const { [key]: _, ...rest } = state;
  return rest;
}

/**
 * Helper function to transform a property in a state object
 *
 * @param state The state object
 * @param key The property key
 * @param transformFn The transformation function
 * @returns A new state object with the property transformed
 */
export function transformProperty<T extends Record<string, unknown>, K extends keyof T, V>(
  state: T,
  key: K,
  transformFn: (value: T[K]) => V
): Omit<T, K> & Record<K, V> {
  return {
    ...state,
    [key]: transformFn(state[key]),
  };
}

import { isEqual } from 'lodash';

interface PersistenceOptions<T> {
  /**
   * The key to use for localStorage
   */
  key: string;

  /**
   * The current version of the state schema
   */
  version: number;

  /**
   * Optional function to migrate state from a previous version
   */
  migrate?: (state: unknown, fromVersion: number) => T;

  /**
   * Optional function to validate the state before loading
   */
  validate?: (state: unknown) => boolean;

  /**
   * Optional function to serialize the state
   * @default JSON.stringify
   */
  serialize?: (state: T) => string;

  /**
   * Optional function to deserialize the state
   * @default JSON.parse
   */
  deserialize?: (serialized: string) => unknown;

  /**
   * Optional function to determine if the state has changed
   * @default lodash.isEqual
   */
  isEqual?: (a: T, b: T) => boolean;

  /**
   * Optional debounce time in milliseconds for saving state
   * @default 1000
   */
  debounceTime?: number;

  /**
   * Optional flag to enable debug logging
   * @default false
   */
  debug?: boolean;
}

interface PersistedData<T> {
  version: number;
  timestamp: number;
  state: T;
}

/**
 * Creates a state persistence manager that handles saving and loading state
 * from localStorage with versioning support.
 *
 * @param options Configuration options for persistence
 * @returns An object with methods to save, load, and clear state
 */
export function createStatePersistence<T>(options: PersistenceOptions<T>) {
  const {
    key,
    version,
    migrate,
    validate,
    serialize = JSON.stringify,
    deserialize = JSON.parse,
    isEqual: equalityFn = isEqual,
    debounceTime = 1000,
    debug = false,
  } = options;

  // Keep a reference to the last saved state to avoid unnecessary saves
  let lastSavedState: T | null = null;
  let saveTimeout: ReturnType<typeof setTimeout> | null = null;

  const log = (message: string, ...args: unknown[]) => {
    if (debug) {
      console.log(`[StatePersistence:${key}] ${message}`, ...args);
    }
  };

  /**
   * Saves the state to localStorage
   *
   * @param state The state to save
   * @param immediate Whether to save immediately or debounce
   * @returns A promise that resolves when the state is saved
   */
  const saveState = (state: T, immediate = false): Promise<void> => {
    return new Promise(resolve => {
      // If the state hasn't changed, don't save it
      if (lastSavedState && equalityFn(state, lastSavedState)) {
        log('State unchanged, skipping save');
        resolve();
        return;
      }

      // Clear any existing timeout
      if (saveTimeout) {
        clearTimeout(saveTimeout);
        saveTimeout = null;
      }

      const performSave = () => {
        try {
          const data: PersistedData<T> = {
            version,
            timestamp: Date.now(),
            state,
          };

          const serialized = serialize(data as unknown as T);
          localStorage.setItem(key, serialized);
          lastSavedState = state;

          log('State saved', { version, timestamp: data.timestamp });
          resolve();
        } catch (error) {
          console.error(`[StatePersistence:${key}] Error saving state:`, error);
          resolve();
        }
      };

      if (immediate) {
        performSave();
      } else {
        saveTimeout = setTimeout(performSave, debounceTime);
      }
    });
  };

  /**
   * Loads the state from localStorage
   *
   * @returns The loaded state, or null if no state was found or it was invalid
   */
  const loadState = (): T | null => {
    try {
      const serialized = localStorage.getItem(key);

      if (!serialized) {
        log('No saved state found');
        return null;
      }

      const data = deserialize(serialized) as PersistedData<T>;

      // Validate the data structure
      if (!data || typeof data !== 'object' || !('version' in data) || !('state' in data)) {
        log('Invalid state format', data);
        return null;
      }

      // Check if we need to migrate
      if (data.version !== version) {
        log(`Version mismatch: saved=${data.version}, current=${version}`);

        if (!migrate) {
          log('No migration function provided, clearing saved state');
          clearState();
          return null;
        }

        try {
          const migratedState = migrate(data.state, data.version);
          log('State migrated successfully', { fromVersion: data.version, toVersion: version });

          // Save the migrated state
          saveState(migratedState, true);
          return migratedState;
        } catch (error) {
          console.error(`[StatePersistence:${key}] Migration error:`, error);
          clearState();
          return null;
        }
      }

      // Validate the state if a validator is provided
      if (validate && !validate(data.state)) {
        log('State validation failed');
        return null;
      }

      log('State loaded successfully', {
        version: data.version,
        age: Date.now() - (data.timestamp || 0),
      });
      lastSavedState = data.state;
      return data.state;
    } catch (error) {
      console.error(`[StatePersistence:${key}] Error loading state:`, error);
      return null;
    }
  };

  /**
   * Clears the saved state from localStorage
   */
  const clearState = (): void => {
    try {
      localStorage.removeItem(key);
      lastSavedState = null;
      log('State cleared');
    } catch (error) {
      console.error(`[StatePersistence:${key}] Error clearing state:`, error);
    }
  };

  return {
    saveState,
    loadState,
    clearState,
  };
}

/**
 * Creates a hook-friendly state persistence manager
 *
 * @param options Configuration options for persistence
 * @returns An object with methods to use in React components
 */
export function createStatePersistenceHook<T>(options: PersistenceOptions<T>) {
  const persistence = createStatePersistence(options);

  return {
    /**
     * Loads the persisted state
     *
     * @returns The loaded state or null
     */
    loadPersistedState: persistence.loadState,

    /**
     * Saves the current state
     *
     * @param state The state to save
     * @param immediate Whether to save immediately
     */
    persistState: persistence.saveState,

    /**
     * Clears the persisted state
     */
    clearPersistedState: persistence.clearState,
  };
}

/**
 * Creates a simple localStorage getter/setter for a specific key
 *
 * @param key The localStorage key
 * @param defaultValue The default value to return if the key doesn't exist
 * @returns An object with get and set methods
 */
export function createLocalStorageItem<T>(key: string, defaultValue: T) {
  return {
    /**
     * Gets the value from localStorage
     *
     * @returns The parsed value or the default value
     */
    get: (): T => {
      try {
        const item = localStorage.getItem(key);
        return item ? JSON.parse(item) : defaultValue;
      } catch (error) {
        console.error(`[LocalStorage:${key}] Error getting item:`, error);
        return defaultValue;
      }
    },

    /**
     * Sets the value in localStorage
     *
     * @param value The value to set
     */
    set: (value: T): void => {
      try {
        localStorage.setItem(key, JSON.stringify(value));
      } catch (error) {
        console.error(`[LocalStorage:${key}] Error setting item:`, error);
      }
    },

    /**
     * Removes the item from localStorage
     */
    remove: (): void => {
      try {
        localStorage.removeItem(key);
      } catch (error) {
        console.error(`[LocalStorage:${key}] Error removing item:`, error);
      }
    },
  };
}

/**
 * @file hookTestingUtils.tsx
 * Provides utilities for testing context hooks.
 *
 * This file implements:
 * 1. Test renderers for hooks with mocked contexts
 * 2. State change simulators for testing hook reactivity
 * 3. Selector usage tracking for performance testing
 * 4. Validation utilities for hook behavior verification
 */

import { act, render, RenderResult } from '@testing-library/react';
import { ReactNode, useEffect, useState } from 'react';
import {
  clearHookPerformanceData,
  getHookPerformanceData,
  HookPerformanceConfig,
} from '../performance/hookPerformanceMonitor';

// Import context providers
import { GameProvider } from '../../contexts/GameContext';
import { ModuleProvider } from '../../contexts/ModuleContext';
import { ResourceRatesProvider } from '../../contexts/ResourceRatesContext';

// Base type for state objects
export type StateObject = Record<string, unknown>;

// Type definitions for the test providers and utilities
export interface TestProviderProps {
  children: ReactNode;
  initialState?: StateObject;
}

/**
 * Mock initial states for different context providers
 */
export interface MockContextStates {
  gameState?: StateObject;
  resourceState?: StateObject;
  moduleState?: StateObject;
}

/**
 * Result from a hook test operation
 */
export interface HookTestResult<T> {
  /**
   * The current result of the hook
   */
  result: T;

  /**
   * The render result from React Testing Library
   */
  renderResult: RenderResult;

  /**
   * Function to re-render the hook with updated context
   */
  rerender: (updatedState?: MockContextStates) => void;

  /**
   * Performance data for the hook
   */
  performanceData: () => ReturnType<typeof getHookPerformanceData> | undefined;

  /**
   * Wait for the next re-render of the hook
   */
  waitForNextRender: () => Promise<void>;

  /**
   * Clean up the test
   */
  cleanup: () => void;
}

/**
 * A component that tracks renders of a hook
 */
function HookTestComponent<T, P extends unknown[]>({
  useHook,
  onResult,
  hookParams = [] as unknown as P,
}: {
  useHook: (...args: P) => T;
  onResult: (result: T) => void;
  hookParams?: P;
}) {
  // Call the hook with provided params
  const hookResult = useHook(...hookParams);

  // Notify the test about the hook result
  useEffect(() => {
    onResult(hookResult);
  }, [hookResult, onResult]);

  return null;
}

/**
 * Create a wrapper with all context providers for testing hooks
 */
export function createContextProviders(mockStates: MockContextStates = {}) {
  return ({ children }: { children: ReactNode }) => (
    <GameProvider initialGameState={mockStates.gameState}>
      <ResourceRatesProvider>
        <ModuleProvider>{children}</ModuleProvider>
      </ResourceRatesProvider>
    </GameProvider>
  );
}

/**
 * Renders a hook with mocked context providers for testing
 *
 * @param useHook The hook to test
 * @param mockStates Initial state for context providers
 * @param hookParams Parameters to pass to the hook
 * @returns Test result object with utilities for testing the hook
 */
export function renderHookWithContexts<T, P extends unknown[]>(
  useHook: (...args: P) => T,
  mockStates: MockContextStates = {},
  hookParams: P = [] as unknown as P
): HookTestResult<T> {
  let latestResult: T;
  let renderCount = 0;
  const renderPromises: Array<{
    resolve: () => void;
    reject: (reason?: unknown) => void;
  }> = [];

  // Function to handle hook result updates
  const handleResult = (result: T) => {
    latestResult = result;
    renderCount++;

    // Resolve any pending render promises
    if (renderPromises.length > 0) {
      const promise = renderPromises.shift();
      if (promise) {
        promise.resolve();
      }
    }
  };

  // Render the hook within context providers
  const renderResult = render(
    <HookTestComponent useHook={useHook} onResult={handleResult} hookParams={hookParams} />,
    { wrapper: createContextProviders(mockStates) }
  );

  // Function to rerender with updated state
  const rerender = (updatedState?: MockContextStates) => {
    act(() => {
      renderResult.rerender(
        <HookTestComponent useHook={useHook} onResult={handleResult} hookParams={hookParams} />
      );
    });
  };

  // Function to wait for the next render
  const waitForNextRender = () => {
    const currentRenderCount = renderCount;
    return new Promise<void>((resolve, reject) => {
      // If a render has already happened since we started waiting, resolve immediately
      if (renderCount > currentRenderCount) {
        resolve();
        return;
      }

      // Otherwise, store the promise to be resolved on next render
      renderPromises.push({ resolve, reject });

      // Set a timeout to reject the promise if no render happens
      setTimeout(() => {
        const index = renderPromises.findIndex(p => p.resolve === resolve);
        if (index !== -1) {
          renderPromises.splice(index, 1);
          reject(new Error('Timed out waiting for hook to render'));
        }
      }, 5000);
    });
  };

  // Function to get performance data for the hook
  const getPerformanceData = () => {
    if (typeof useHook.name === 'string') {
      return getHookPerformanceData(useHook.name);
    }
    return undefined;
  };

  // Clean up function
  const cleanup = () => {
    renderResult.unmount();
    if (typeof useHook.name === 'string') {
      clearHookPerformanceData(useHook.name);
    }
    // Reject any pending render promises
    renderPromises.forEach(p => p.reject(new Error('Test was cleaned up')));
    renderPromises.length = 0;
  };

  return {
    result: latestResult!,
    renderResult,
    rerender,
    performanceData: getPerformanceData,
    waitForNextRender,
    cleanup,
  };
}

/**
 * Mocks a game state update for testing hooks
 *
 * @param renderResult The render result from renderHookWithContexts
 * @param stateUpdates State updates to apply
 */
export function mockGameStateUpdate(renderResult: RenderResult, stateUpdates: StateObject) {
  const gameContext = renderResult.container.querySelector('[data-testid="game-context"]');
  if (!gameContext) {
    throw new Error('Game context not found');
  }

  act(() => {
    // Dispatch a state update event to the game context
    const updateEvent = new CustomEvent('state-update', {
      detail: stateUpdates,
    });
    gameContext.dispatchEvent(updateEvent);
  });
}

