/**
 * Chart axes configuration
 */
export interface ChartAxes {
  x: {
    type: 'linear' | 'time' | 'category' | 'log';
    label?: string;
    min?: number | string | Date;
    max?: number | string | Date;
    tickCount?: number;
    tickFormat?: (value: any) => string;
    grid?: boolean;
  };
  y: {
    type: 'linear' | 'log';
    label?: string;
    min?: number;
    max?: number;
    tickCount?: number;
    tickFormat?: (value: number) => string;
    grid?: boolean;
  };
}

/**
 * Chart legend configuration
 */
export interface ChartLegend {
  visible: boolean;
  position: 'top' | 'right' | 'bottom' | 'left';
  align?: 'start' | 'center' | 'end';
}

/**
 * Chart tooltip configuration
 */
export interface ChartTooltip {
  enabled: boolean;
  mode: 'point' | 'nearest' | 'dataset';
  intersect?: boolean;
  format?: (point: ChartDataPoint, dataset: ChartData['datasets'][0]) => string;
}

/**
 * Chart animation configuration
 */
export interface ChartAnimation {
  enabled: boolean;
  duration?: number;
  easing?: 'linear' | 'easeInOut' | 'easeIn' | 'easeOut';
}

/**
 * Chart options interface
 */
export interface ChartOptions {
  width?: number | string;
  height?: number | string;
  renderer?: 'canvas' | 'svg' | 'webgl';
  responsive?: boolean;
  maintainAspectRatio?: boolean;
  axes?: ChartAxes;
  legend?: ChartLegend;
  tooltip?: ChartTooltip;
  animation?: ChartAnimation;
  backgroundColor?: string;
  padding?: {
    top?: number;
    right?: number;
    bottom?: number;
    left?: number;
  };
  enablePanning?: boolean;
  enableZooming?: boolean;
  theme?: 'light' | 'dark' | 'auto';
  memoryOptimized?: boolean;
  renderOptimization?: boolean;
  optimizationThreshold?: number;
}

/**
 * Default chart options
 */
const DEFAULT_CHART_OPTIONS: ChartOptions = {
  width: '100%',
  height: 300,
  renderer: 'canvas',
  responsive: true,
  maintainAspectRatio: true,
  axes: {
    x: {
      type: 'linear',
      grid: true,
    },
    y: {
      type: 'linear',
      grid: true,
    }
  },
  legend: {
    visible: true,
    position: 'top',
    align: 'center',
  },
  tooltip: {
    enabled: true,
    mode: 'nearest',
    intersect: true,
  },
  animation: {
    enabled: true,
    duration: 300,
    easing: 'easeInOut',
  },
  backgroundColor: 'transparent',
  padding: {
    top: 10,
    right: 10,
    bottom: 20,
    left: 30,
  },
  enablePanning: false,
  enableZooming: false,
  theme: 'light',
  memoryOptimized: false,
  renderOptimization: true,
  optimizationThreshold: 1000,
};

/**
 * Chart renderer interface
 */
export interface ChartRenderer {
  render: (
    container: HTMLElement,
    data: ChartData,
    options: ChartOptions,
    type: ChartType
  ) => void;
  update: (
    container: HTMLElement,
    data: ChartData,
    options: ChartOptions,
    type: ChartType
  ) => void;
  destroy: () => void;
  getStatus: () => { isInitialized: boolean; lastRenderTime?: number };
}

/**
 * Chart type
 */
export type ChartType = 'line' | 'bar' | 'scatter' | 'area' | 'pie' | 'radar' | 'heatmap';

/**
 * Chart component props
 */
export interface ChartProps {
  data: ChartData;
  options?: Partial<ChartOptions>;
  type: ChartType;
  className?: string;
  onRender?: (renderer: ChartRenderer) => void;
  onError?: (error: Error) => void;
}

/**
 * Chart component that uses a strategy pattern for different rendering methods
 */
export const Chart: React.FC<ChartProps> = ({
  data,
  options = {},
  type,
  className = '',
  onRender,
  onError,
}) => {
  // Merge options with defaults
  const mergedOptions: ChartOptions = useMemo(() => ({
    ...DEFAULT_CHART_OPTIONS,
    ...options,
  }), [options]);

  // Container ref for rendering
  const containerRef = useRef<HTMLDivElement>(null);

  // Chart renderer ref
  const rendererRef = useRef<ChartRenderer | null>(null);

  // Error state
  const [error, setError] = useState<Error | null>(null);

  // Auto-select renderer based on data size and device capabilities if not specified
  const renderer = useMemo(() => {
    // Count total data points
    const totalDataPoints = data.datasets.reduce((acc, dataset) => acc + dataset.data.length, 0);

    // If renderer is explicitly set, use that
    if (mergedOptions.renderer) {
      return mergedOptions.renderer;
    }

    // Auto-select based on data size and optimization settings
    if (mergedOptions.memoryOptimized || totalDataPoints > (mergedOptions.optimizationThreshold || 1000)) {
      // Check if WebGL is available
      try {
        const canvas = document.createElement('canvas');
        const gl = canvas.getContext('webgl') || canvas.getContext('experimental-webgl');
        if (gl) {
          return 'webgl';
        }
      } catch (e) {
        // WebGL not available, fall back to canvas
      }

      return 'canvas';
    }

    // For smaller datasets, SVG provides better interactivity
    return 'svg';
  }, [mergedOptions.renderer, mergedOptions.memoryOptimized, mergedOptions.optimizationThreshold, data]);

  // Initialize renderer
  useEffect(() => {
    if (!containerRef.current) return;

    try {
      // Destroy previous renderer if it exists
      if (rendererRef.current) {
        rendererRef.current.destroy();
        rendererRef.current = null;
      }

      // Create new renderer based on selected strategy
      switch (renderer) {
        case 'canvas':
          rendererRef.current = new CanvasRenderer();
          break;
        case 'webgl':
          rendererRef.current = new WebGLRenderer();
          break;
        case 'svg':
        default:
          rendererRef.current = new SVGRenderer();
          break;
      }

      // Initial render
      rendererRef.current.render(containerRef.current, data, mergedOptions, type);

      // Call onRender callback if provided
      if (onRender) {
        onRender(rendererRef.current);
      }

      // Clear any previous errors
      if (error) {
        setError(null);
      }
    } catch (err) {
      const error = err instanceof Error ? err : new Error(String(err));

      // Log error
      errorLoggingService.logError(error, ErrorType.RUNTIME, undefined, {
        component: 'Chart',
        renderer,
        chartType: type,
        dataSize: data.datasets.reduce((acc, dataset) => acc + dataset.data.length, 0),
      });

      // Set error state
      setError(error);

      // Call onError callback if provided
      if (onError) {
        onError(error);
      }
    }

    // Cleanup
    return () => {
      if (rendererRef.current) {
        rendererRef.current.destroy();
        rendererRef.current = null;
      }
    };
  }, [renderer, type]);

  // Update chart when data or options change
  useEffect(() => {
    if (!containerRef.current || !rendererRef.current) return;

    try {
      rendererRef.current.update(containerRef.current, data, mergedOptions, type);

      // Clear any previous errors
      if (error) {
        setError(null);
      }
    } catch (err) {
      const error = err instanceof Error ? err : new Error(String(err));

      // Log error
      errorLoggingService.logError(error, ErrorType.RUNTIME, undefined, {
        component: 'Chart',
        renderer,
        chartType: type,
        dataSize: data.datasets.reduce((acc, dataset) => acc + dataset.data.length, 0),
        action: 'update',
      });

      // Set error state
      setError(error);

      // Call onError callback if provided
      if (onError) {
        onError(error);
      }
    }
  }, [data, mergedOptions]);

  // Handle container resize
  useEffect(() => {
    if (!containerRef.current || !rendererRef.current || !mergedOptions.responsive) return;

    const resizeObserver = new ResizeObserver(() => {
      if (containerRef.current && rendererRef.current) {
        try {
          rendererRef.current.update(containerRef.current, data, mergedOptions, type);
        } catch (err) {
          console.error('Error resizing chart:', err);
        }
      }
    });

    resizeObserver.observe(containerRef.current);

    return () => {
      resizeObserver.disconnect();
    };
  }, [mergedOptions.responsive]);

  // Fallback UI for errors
  if (error) {
    return (
      <div 
        className={`chart-error ${className}`}
        style={{
          width: mergedOptions.width,
          height: mergedOptions.height,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          border: '1px solid #ff6b6b',
          borderRadius: '4px',
          padding: '1rem',
          backgroundColor: '#fff1f1',
          color: '#d63031',
        }}
      >
        <h4>Chart Error</h4>
        <p style={{ fontSize: '0.875rem' }}>{error.message}</p>
        <button 
          onClick={() => {
            setError(null);
            if (containerRef.current && rendererRef.current) {
              try {
                rendererRef.current.render(containerRef.current, data, mergedOptions, type);
              } catch (err) {
                setError(err instanceof Error ? err : new Error(String(err)));
              }
            }
          }}
          style={{
            marginTop: '0.5rem',
            padding: '0.25rem 0.75rem',
            border: '1px solid #d63031',
            borderRadius: '4px',
            backgroundColor: 'white',
            color: '#d63031',
            cursor: 'pointer',
          }}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div
      ref={containerRef}
      className={`chart-container ${className}`}
      style={{
        width: mergedOptions.width,
        height: mergedOptions.height,
        position: 'relative',
      }}
      data-chart-type={type}
      data-chart-renderer={renderer}
    />
  );
};

export default Chart;

import { ChartData, ChartOptions, ChartRenderer, ChartType } from '../Chart';

/**
 * Canvas-based chart renderer implementation.
 * Uses the HTML Canvas API for efficient rendering of charts with large datasets.
 */
export class CanvasRenderer implements ChartRenderer {
  private canvas: HTMLCanvasElement | null = null;
  private ctx: CanvasRenderingContext2D | null = null;
  private containerWidth = 0;
  private containerHeight = 0;
  private resolutionScale = window.devicePixelRatio || 1;
  private animationFrame: number | null = null;
  private isInitialized = false;
  private lastRenderTime = 0;
  private tooltipElement: HTMLDivElement | null = null;

  private theme = {
    light: {
      textColor: '#333333',
      gridColor: '#e0e0e0',
      axisColor: '#666666',
      backgroundColor: 'transparent'
    },
    dark: {
      textColor: '#e0e0e0',
      gridColor: '#444444',
      axisColor: '#999999',
      backgroundColor: 'transparent'
    }
  };

  /**
   * Render a chart onto the container element
   */
  public render(
    container: HTMLElement,
    data: ChartData,
    options: ChartOptions,
    type: ChartType
  ): void {
    this.initialize(container, options);
    this.update(container, data, options, type);
  }

  /**
   * Updates the chart with new data or options
   */
  public update(
    container: HTMLElement,
    data: ChartData,
    options: ChartOptions,
    type: ChartType
  ): void {
    if (!this.isInitialized) {
      this.initialize(container, options);
    }

    // Cancel any pending animation frame
    if (this.animationFrame !== null) {
      cancelAnimationFrame(this.animationFrame);
    }

    const startTime = performance.now();

    // Update chart dimensions if container size has changed
    this.updateDimensions(container);

    // Render the chart based on type
    this.animationFrame = requestAnimationFrame(() => {
      if (!this.ctx || !this.canvas) return;

      // Clear the canvas
      this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

      // Set background color
      const themeColors = options.theme === 'dark' ? this.theme.dark : this.theme.light;
      this.ctx.fillStyle = options.backgroundColor || themeColors.backgroundColor;
      if (this.ctx.fillStyle !== 'transparent') {
        this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
      }

      // Calculate chart area with padding
      const padding = options.padding || {};
      const chartArea = {
        left: (padding.left || 0) * this.resolutionScale,
        right: this.canvas.width - (padding.right || 0) * this.resolutionScale,
        top: (padding.top || 0) * this.resolutionScale,
        bottom: this.canvas.height - (padding.bottom || 0) * this.resolutionScale
      };

      // Render based on chart type
      switch (type) {
        case 'line':
          this.renderLineChart(data, options, chartArea);
          break;
        case 'bar':
          this.renderBarChart(data, options, chartArea);
          break;
        case 'scatter':
          this.renderScatterChart(data, options, chartArea);
          break;
        case 'area':
          this.renderAreaChart(data, options, chartArea);
          break;
        case 'pie':
          this.renderPieChart(data, options);
          break;
        case 'radar':
          this.renderRadarChart(data, options);
          break;
        case 'heatmap':
          this.renderHeatmapChart(data, options, chartArea);
          break;
        default:
          throw new Error(`Unsupported chart type: ${type}`);
      }

      // Render axes
      if (type !== 'pie' && type !== 'radar') {
        this.renderAxes(data, options, chartArea);
      }

      // Render legend if enabled
      if (options.legend?.visible) {
        this.renderLegend(data, options, chartArea);
      }

      this.lastRenderTime = performance.now() - startTime;
    });

    // Set up tooltip if enabled
    if (options.tooltip?.enabled) {
      this.setupTooltip(container, data, options);
    } else if (this.tooltipElement) {
      this.tooltipElement.remove();
      this.tooltipElement = null;
    }
  }

  /**
   * Destroys the renderer, cleaning up any resources
   */
  public destroy(): void {
    if (this.animationFrame !== null) {
      cancelAnimationFrame(this.animationFrame);
      this.animationFrame = null;
    }

    if (this.tooltipElement) {
      this.tooltipElement.remove();
      this.tooltipElement = null;
    }

    this.canvas = null;
    this.ctx = null;
    this.isInitialized = false;
  }

  /**
   * Returns the current status of the renderer
   */
  public getStatus(): { isInitialized: boolean; lastRenderTime?: number } {
    return {
      isInitialized: this.isInitialized,
      lastRenderTime: this.lastRenderTime
    };
  }

  /**
   * Initialize the canvas renderer
   */
  private initialize(container: HTMLElement, options: ChartOptions): void {
    // Clean existing canvas if any
    const existingCanvas = container.querySelector('canvas');
    if (existingCanvas) {
      container.removeChild(existingCanvas);
    }

    // Create new canvas
    this.canvas = document.createElement('canvas');
    this.canvas.style.display = 'block';
    this.canvas.style.width = '100%';
    this.canvas.style.height = '100%';
    container.appendChild(this.canvas);

    // Get context
    this.ctx = this.canvas.getContext('2d');
    if (!this.ctx) {
      throw new Error('Could not get canvas context');
    }

    // Set dimensions
    this.updateDimensions(container);

    this.isInitialized = true;
  }

  /**
   * Update canvas dimensions based on container size
   */
  private updateDimensions(container: HTMLElement): void {
    if (!this.canvas || !this.ctx) return;

    const rect = container.getBoundingClientRect();
    this.containerWidth = rect.width;
    this.containerHeight = rect.height;

    // Set canvas dimensions with higher resolution for retina displays
    this.canvas.width = this.containerWidth * this.resolutionScale;
    this.canvas.height = this.containerHeight * this.resolutionScale;

    // Scale context
    this.ctx.scale(this.resolutionScale, this.resolutionScale);
  }

  /**
   * Render line chart
   */
  private renderLineChart(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.ctx) return;

    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };

    // Calculate scales
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);

    // Draw grid lines if enabled
    if (xAxis.grid || yAxis.grid) {
      this.drawGrid(scales, chartArea, options);
    }

    // Draw each dataset
    datasets.forEach((dataset, datasetIndex) => {
      const points: { x: number; y: number }[] = [];

      // Map data points to canvas coordinates
      dataset.data.forEach(point => {
        const x = this.mapValueToPixel(point.x, scales.x, chartArea.left, chartArea.right);
        const y = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);
        points.push({ x, y });
      });

      if (points.length === 0) return;

      // Draw lines connecting points
      this.ctx!.beginPath();
      this.ctx!.moveTo(points[0].x, points[0].y);

      for (let i = 1; i < points.length; i++) {
        this.ctx!.lineTo(points[i].x, points[i].y);
      }

      this.ctx!.strokeStyle = dataset.color || this.getDefaultColor(datasetIndex);
      this.ctx!.lineWidth = 2;
      this.ctx!.stroke();

      // Draw data points
      points.forEach(point => {
        this.ctx!.beginPath();
        this.ctx!.arc(point.x, point.y, 3, 0, Math.PI * 2);
        this.ctx!.fillStyle = dataset.color || this.getDefaultColor(datasetIndex);
        this.ctx!.fill();
      });
    });
  }

  /**
   * Render bar chart
   */
  private renderBarChart(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.ctx) return;

    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'category' };
    const yAxis = options.axes?.y || { type: 'linear' };

    // Calculate scales
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);

    // Draw grid lines if enabled
    if (xAxis.grid || yAxis.grid) {
      this.drawGrid(scales, chartArea, options);
    }

    // Calculate bar width based on the number of datasets and data points
    const allLabels = this.getAllXValues(data);
    const totalBarGroups = allLabels.length;
    const barGroupWidth = (chartArea.right - chartArea.left) / (totalBarGroups + 1);
    const barWidth = barGroupWidth * 0.8 / datasets.length;

    // Draw each dataset
    datasets.forEach((dataset, datasetIndex) => {
      dataset.data.forEach(point => {
        const xIndex = allLabels.indexOf(String(point.x));
        const x = chartArea.left + (xIndex + 1) * barGroupWidth - barGroupWidth * 0.4 + datasetIndex * barWidth;
        const yZero = this.mapValueToPixel(0, scales.y, chartArea.bottom, chartArea.top, true);
        const y = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);

        this.ctx!.fillStyle = dataset.color || this.getDefaultColor(datasetIndex);
        this.ctx!.fillRect(x, Math.min(y, yZero), barWidth, Math.abs(y - yZero));
      });
    });
  }

  /**
   * Render scatter chart
   */
  private renderScatterChart(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.ctx) return;

    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };

    // Calculate scales
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);

    // Draw grid lines if enabled
    if (xAxis.grid || yAxis.grid) {
      this.drawGrid(scales, chartArea, options);
    }

    // Draw each dataset
    datasets.forEach((dataset, datasetIndex) => {
      dataset.data.forEach(point => {
        const x = this.mapValueToPixel(point.x, scales.x, chartArea.left, chartArea.right);
        const y = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);

        this.ctx!.beginPath();
        this.ctx!.arc(x, y, point.radius || 5, 0, Math.PI * 2);
        this.ctx!.fillStyle = dataset.color || this.getDefaultColor(datasetIndex);
        this.ctx!.fill();
      });
    });
  }

  /**
   * Render area chart
   */
  private renderAreaChart(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.ctx) return;

    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };

    // Calculate scales
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);

    // Draw grid lines if enabled
    if (xAxis.grid || yAxis.grid) {
      this.drawGrid(scales, chartArea, options);
    }

    // Draw each dataset
    datasets.forEach((dataset, datasetIndex) => {
      const points: { x: number; y: number }[] = [];

      // Map data points to canvas coordinates
      dataset.data.forEach(point => {
        const x = this.mapValueToPixel(point.x, scales.x, chartArea.left, chartArea.right);
        const y = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);
        points.push({ x, y });
      });

      if (points.length === 0) return;

      // Draw filled area
      this.ctx!.beginPath();
      this.ctx!.moveTo(points[0].x, chartArea.bottom);
      this.ctx!.lineTo(points[0].x, points[0].y);

      for (let i = 1; i < points.length; i++) {
        this.ctx!.lineTo(points[i].x, points[i].y);
      }

      this.ctx!.lineTo(points[points.length - 1].x, chartArea.bottom);
      this.ctx!.closePath();

      // Create gradient fill
      const gradient = this.ctx!.createLinearGradient(0, chartArea.top, 0, chartArea.bottom);
      const color = dataset.color || this.getDefaultColor(datasetIndex);
      gradient.addColorStop(0, this.hexToRgba(color, 0.6));
      gradient.addColorStop(1, this.hexToRgba(color, 0.1));

      this.ctx!.fillStyle = gradient;
      this.ctx!.fill();

      // Draw the line on top
      this.ctx!.beginPath();
      this.ctx!.moveTo(points[0].x, points[0].y);

      for (let i = 1; i < points.length; i++) {
        this.ctx!.lineTo(points[i].x, points[i].y);
      }

      this.ctx!.strokeStyle = color;
      this.ctx!.lineWidth = 2;
      this.ctx!.stroke();
    });
  }

  /**
   * Render pie chart
   */
  private renderPieChart(data: ChartData, options: ChartOptions): void {
    if (!this.ctx || !this.canvas) return;

    const { datasets } = data;
    const centerX = this.canvas.width / (2 * this.resolutionScale);
    const centerY = this.canvas.height / (2 * this.resolutionScale);
    const radius = Math.min(centerX, centerY) * 0.7;

    if (datasets.length === 0 || datasets[0].data.length === 0) return;

    // Get the first dataset for pie chart
    const dataset = datasets[0];

    // Calculate total value for proportions
    const total = dataset.data.reduce((sum, point) => sum + point.y, 0);
    if (total <= 0) return;

    // Draw pie slices
    let startAngle = 0;
    dataset.data.forEach((point, i) => {
      const sliceAngle = (point.y / total) * (Math.PI * 2);
      const endAngle = startAngle + sliceAngle;

      this.ctx!.beginPath();
      this.ctx!.moveTo(centerX, centerY);
      this.ctx!.arc(centerX, centerY, radius, startAngle, endAngle);
      this.ctx!.closePath();

      this.ctx!.fillStyle = point.color || this.getDefaultColor(i);
      this.ctx!.fill();

      // Draw slice border
      this.ctx!.strokeStyle = '#fff';
      this.ctx!.lineWidth = 1;
      this.ctx!.stroke();

      // Draw label if there's enough space (slices > 5% of total)
      if (sliceAngle > 0.1) {
        const labelAngle = startAngle + sliceAngle / 2;
        const labelRadius = radius * 0.7;
        const labelX = centerX + Math.cos(labelAngle) * labelRadius;
        const labelY = centerY + Math.sin(labelAngle) * labelRadius;

        this.ctx!.fillStyle = '#fff';
        this.ctx!.font = '12px Arial';
        this.ctx!.textAlign = 'center';
        this.ctx!.textBaseline = 'middle';
        this.ctx!.fillText(point.x.toString(), labelX, labelY);
      }

      startAngle = endAngle;
    });
  }

  /**
   * Render radar chart
   */
  private renderRadarChart(data: ChartData, options: ChartOptions): void {
    if (!this.ctx || !this.canvas) return;

    const { datasets } = data;
    if (datasets.length === 0 || datasets[0].data.length === 0) return;

    const centerX = this.canvas.width / (2 * this.resolutionScale);
    const centerY = this.canvas.height / (2 * this.resolutionScale);
    const radius = Math.min(centerX, centerY) * 0.7;

    // Find all unique labels (angles) from all datasets
    const allLabels = new Set<string>();
    datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        allLabels.add(String(point.x));
      });
    });

    const labels = Array.from(allLabels);
    const angleStep = (Math.PI * 2) / labels.length;

    // Draw background grid (circles and lines)
    this.ctx!.strokeStyle = options.theme === 'dark' ? this.theme.dark.gridColor : this.theme.light.gridColor;
    this.ctx!.lineWidth = 0.5;

    // Draw circular grid lines
    for (let i = 1; i <= 5; i++) {
      const gridRadius = radius * (i / 5);
      this.ctx!.beginPath();
      this.ctx!.arc(centerX, centerY, gridRadius, 0, Math.PI * 2);
      this.ctx!.stroke();
    }

    // Draw radial grid lines
    labels.forEach((_, i) => {
      const angle = i * angleStep - Math.PI / 2; // Start from top
      this.ctx!.beginPath();
      this.ctx!.moveTo(centerX, centerY);
      this.ctx!.lineTo(
        centerX + Math.cos(angle) * radius,
        centerY + Math.sin(angle) * radius
      );
      this.ctx!.stroke();

      // Draw labels
      const labelX = centerX + Math.cos(angle) * (radius + 15);
      const labelY = centerY + Math.sin(angle) * (radius + 15);
      this.ctx!.fillStyle = options.theme === 'dark' ? this.theme.dark.textColor : this.theme.light.textColor;
      this.ctx!.font = '12px Arial';
      this.ctx!.textAlign = 'center';
      this.ctx!.textBaseline = 'middle';
      this.ctx!.fillText(labels[i], labelX, labelY);
    });

    // Find max value for scaling
    const maxValue = datasets.reduce((max, dataset) => {
      const datasetMax = dataset.data.reduce((m, point) => Math.max(m, point.y), 0);
      return Math.max(max, datasetMax);
    }, 0);

    // Draw each dataset
    datasets.forEach((dataset, datasetIndex) => {
      const dataMap = new Map<string, number>();
      dataset.data.forEach(point => {
        dataMap.set(String(point.x), point.y);
      });

      // Create points for the radar shape
      const points: { x: number; y: number }[] = [];
      labels.forEach((label, i) => {
        const value = dataMap.get(label) || 0;
        const valueRatio = Math.max(0, Math.min(1, value / maxValue));
        const angle = i * angleStep - Math.PI / 2; // Start from top
        const x = centerX + Math.cos(angle) * radius * valueRatio;
        const y = centerY + Math.sin(angle) * radius * valueRatio;
        points.push({ x, y });
      });

      if (points.length < 3) return;

      // Draw filled area
      this.ctx!.beginPath();
      this.ctx!.moveTo(points[0].x, points[0].y);

      for (let i = 1; i < points.length; i++) {
        this.ctx!.lineTo(points[i].x, points[i].y);
      }

      this.ctx!.closePath();

      const color = dataset.color || this.getDefaultColor(datasetIndex);
      this.ctx!.fillStyle = this.hexToRgba(color, 0.2);
      this.ctx!.fill();

      // Draw outline
      this.ctx!.strokeStyle = color;
      this.ctx!.lineWidth = 2;
      this.ctx!.stroke();

      // Draw points
      points.forEach(point => {
        this.ctx!.beginPath();
        this.ctx!.arc(point.x, point.y, 3, 0, Math.PI * 2);
        this.ctx!.fillStyle = color;
        this.ctx!.fill();
      });
    });
  }

  /**
   * Render heatmap chart
   */
  private renderHeatmapChart(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.ctx) return;

    const { datasets } = data;
    if (datasets.length === 0 || datasets[0].data.length === 0) return;

    const xAxis = options.axes?.x || { type: 'category' };
    const yAxis = options.axes?.y || { type: 'category' };

    // For heatmap, we need to find all unique x and y values
    const uniqueX = new Set<string>();
    const uniqueY = new Set<string>();

    datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        uniqueX.add(String(point.x));
        uniqueY.add(String(point.y));
      });
    });

    const xValues = Array.from(uniqueX).sort();
    const yValues = Array.from(uniqueY).sort();

    // Calculate cell dimensions
    const cellWidth = (chartArea.right - chartArea.left) / xValues.length;
    const cellHeight = (chartArea.bottom - chartArea.top) / yValues.length;

    // Find min and max values for color scaling
    let minValue = Infinity;
    let maxValue = -Infinity;

    datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        const value = point.value !== undefined ? point.value : point.y;
        minValue = Math.min(minValue, value);
        maxValue = Math.max(maxValue, value);
      });
    });

    // Draw grid
    this.ctx.strokeStyle = options.theme === 'dark' ? this.theme.dark.gridColor : this.theme.light.gridColor;
    this.ctx.lineWidth = 0.5;

    // Vertical grid lines
    for (let i = 0; i <= xValues.length; i++) {
      const x = chartArea.left + i * cellWidth;
      this.ctx.beginPath();
      this.ctx.moveTo(x, chartArea.top);
      this.ctx.lineTo(x, chartArea.bottom);
      this.ctx.stroke();
    }

    // Horizontal grid lines
    for (let i = 0; i <= yValues.length; i++) {
      const y = chartArea.top + i * cellHeight;
      this.ctx.beginPath();
      this.ctx.moveTo(chartArea.left, y);
      this.ctx.lineTo(chartArea.right, y);
      this.ctx.stroke();
    }

    // Draw cells
    datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        const xIndex = xValues.indexOf(String(point.x));
        const yIndex = yValues.indexOf(String(point.y));

        if (xIndex === -1 || yIndex === -1) return;

        const value = point.value !== undefined ? point.value : point.y;
        const normalizedValue = (value - minValue) / (maxValue - minValue);

        const x = chartArea.left + xIndex * cellWidth;
        const y = chartArea.top + yIndex * cellHeight;

        // Generate heatmap color
        this.ctx!.fillStyle = this.getHeatmapColor(normalizedValue);
        this.ctx!.fillRect(x, y, cellWidth, cellHeight);
      });
    });

    // Draw axes
    this.renderCategoryAxes(xValues, yValues, chartArea, options);
  }

  /**
   * Render axes for charts
   */
  private renderAxes(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.ctx) return;

    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);

    // Draw x-axis
    this.ctx.beginPath();
    this.ctx.moveTo(chartArea.left, chartArea.bottom);
    this.ctx.lineTo(chartArea.right, chartArea.bottom);
    this.ctx.strokeStyle = options.theme === 'dark' ? this.theme.dark.axisColor : this.theme.light.axisColor;
    this.ctx.lineWidth = 1;
    this.ctx.stroke();

    // Draw y-axis
    this.ctx.beginPath();
    this.ctx.moveTo(chartArea.left, chartArea.top);
    this.ctx.lineTo(chartArea.left, chartArea.bottom);
    this.ctx.stroke();

    // Draw x-axis ticks and labels
    const xTickCount = xAxis.tickCount || 5;
    const xStep = (scales.x.max - scales.x.min) / (xTickCount - 1);

    for (let i = 0; i < xTickCount; i++) {
      const value = scales.x.min + i * xStep;
      const x = this.mapValueToPixel(value, scales.x, chartArea.left, chartArea.right);
      const y = chartArea.bottom;

      // Draw tick
      this.ctx.beginPath();
      this.ctx.moveTo(x, y);
      this.ctx.lineTo(x, y + 5);
      this.ctx.stroke();

      // Draw label
      this.ctx.fillStyle = options.theme === 'dark' ? this.theme.dark.textColor : this.theme.light.textColor;
      this.ctx.font = '12px Arial';
      this.ctx.textAlign = 'center';
      this.ctx.textBaseline = 'top';

      let tickLabel = String(value);
      if (xAxis.tickFormat) {
        tickLabel = xAxis.tickFormat(value);
      } else if (xAxis.type === 'time' && value instanceof Date) {
        tickLabel = value.toLocaleDateString();
      }

      this.ctx.fillText(tickLabel, x, y + 8);
    }

    // Draw y-axis ticks and labels
    const yTickCount = yAxis.tickCount || 5;
    const yStep = (scales.y.max - scales.y.min) / (yTickCount - 1);

    for (let i = 0; i < yTickCount; i++) {
      const value = scales.y.min + i * yStep;
      const x = chartArea.left;
      const y = this.mapValueToPixel(value, scales.y, chartArea.bottom, chartArea.top, true);

      // Draw tick
      this.ctx.beginPath();
      this.ctx.moveTo(x, y);
      this.ctx.lineTo(x - 5, y);
      this.ctx.stroke();

      // Draw label
      this.ctx.fillStyle = options.theme === 'dark' ? this.theme.dark.textColor : this.theme.light.textColor;
      this.ctx.font = '12px Arial';
      this.ctx.textAlign = 'right';
      this.ctx.textBaseline = 'middle';

      let tickLabel = String(value);
      if (yAxis.tickFormat) {
        tickLabel = yAxis.tickFormat(value);
      }

      this.ctx.fillText(tickLabel, x - 8, y);
    }

    // Draw axis labels if provided
    if (xAxis.label) {
      this.ctx.fillStyle = options.theme === 'dark' ? this.theme.dark.textColor : this.theme.light.textColor;
      this.ctx.font = 'bold 14px Arial';
      this.ctx.textAlign = 'center';
      this.ctx.textBaseline = 'bottom';
      this.ctx.fillText(xAxis.label, (chartArea.left + chartArea.right) / 2, this.canvas!.height / this.resolutionScale - 5);
    }

    if (yAxis.label) {
      this.ctx.save();
      this.ctx.translate(10, (chartArea.top + chartArea.bottom) / 2);
      this.ctx.rotate(-Math.PI / 2);
      this.ctx.fillStyle = options.theme === 'dark' ? this.theme.dark.textColor : this.theme.light.textColor;
      this.ctx.font = 'bold 14px Arial';
      this.ctx.textAlign = 'center';
      this.ctx.textBaseline = 'bottom';
      this.ctx.fillText(yAxis.label, 0, 0);
      this.ctx.restore();
    }
  }

  /**
   * Render category axes for heatmap
   */
  private renderCategoryAxes(
    xCategories: string[],
    yCategories: string[],
    chartArea: any,
    options: ChartOptions
  ): void {
    if (!this.ctx) return;

    const cellWidth = (chartArea.right - chartArea.left) / xCategories.length;
    const cellHeight = (chartArea.bottom - chartArea.top) / yCategories.length;

    // X-axis labels
    this.ctx.fillStyle = options.theme === 'dark' ? this.theme.dark.textColor : this.theme.light.textColor;
    this.ctx.font = '12px Arial';
    this.ctx.textAlign = 'center';
    this.ctx.textBaseline = 'top';

    xCategories.forEach((category, i) => {
      const x = chartArea.left + (i + 0.5) * cellWidth;
      const y = chartArea.bottom + 5;
      this.ctx!.fillText(category, x, y);
    });

    // Y-axis labels
    this.ctx.textAlign = 'right';
    this.ctx.textBaseline = 'middle';

    yCategories.forEach((category, i) => {
      const x = chartArea.left - 5;
      const y = chartArea.top + (i + 0.5) * cellHeight;
      this.ctx!.fillText(category, x, y);
    });
  }

  /**
   * Draw grid lines
   */
  private drawGrid(scales: any, chartArea: any, options: ChartOptions): void {
    if (!this.ctx) return;

    const xAxis = options.axes?.x || { type: 'linear', grid: true };
    const yAxis = options.axes?.y || { type: 'linear', grid: true };

    this.ctx.strokeStyle = options.theme === 'dark' ? this.theme.dark.gridColor : this.theme.light.gridColor;
    this.ctx.lineWidth = 0.5;

    // Draw x-axis grid lines
    if (yAxis.grid) {
      const xTickCount = xAxis.tickCount || 5;
      const xStep = (scales.x.max - scales.x.min) / (xTickCount - 1);

      for (let i = 0; i < xTickCount; i++) {
        const value = scales.x.min + i * xStep;
        const x = this.mapValueToPixel(value, scales.x, chartArea.left, chartArea.right);

        this.ctx.beginPath();
        this.ctx.moveTo(x, chartArea.top);
        this.ctx.lineTo(x, chartArea.bottom);
        this.ctx.stroke();
      }
    }

    // Draw y-axis grid lines
    if (xAxis.grid) {
      const yTickCount = yAxis.tickCount || 5;
      const yStep = (scales.y.max - scales.y.min) / (yTickCount - 1);

      for (let i = 0; i < yTickCount; i++) {
        const value = scales.y.min + i * yStep;
        const y = this.mapValueToPixel(value, scales.y, chartArea.bottom, chartArea.top, true);

        this.ctx.beginPath();
        this.ctx.moveTo(chartArea.left, y);
        this.ctx.lineTo(chartArea.right, y);
        this.ctx.stroke();
      }
    }
  }

  /**
   * Render legend
   */
  private renderLegend(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.ctx || !this.canvas) return;

    const { datasets } = data;
    const legendOptions = options.legend || { visible: true, position: 'top' };

    if (!legendOptions.visible || datasets.length === 0) return;

    const padding = 10;
    const itemHeight = 20;
    const itemWidth = 80;
    const itemsPerRow = Math.floor((chartArea.right - chartArea.left) / itemWidth);
    const rows = Math.ceil(datasets.length / itemsPerRow);

    let startX, startY, legendWidth, legendHeight;

    legendWidth = Math.min(datasets.length, itemsPerRow) * itemWidth;
    legendHeight = rows * itemHeight;

    switch (legendOptions.position) {
      case 'top':
        startX = (chartArea.left + chartArea.right - legendWidth) / 2;
        startY = chartArea.top - legendHeight - padding;
        break;
      case 'bottom':
        startX = (chartArea.left + chartArea.right - legendWidth) / 2;
        startY = chartArea.bottom + padding;
        break;
      case 'left':
        startX = chartArea.left - legendWidth - padding;
        startY = (chartArea.top + chartArea.bottom - legendHeight) / 2;
        break;
      case 'right':
        startX = chartArea.right + padding;
        startY = (chartArea.top + chartArea.bottom - legendHeight) / 2;
        break;
      default:
        startX = (chartArea.left + chartArea.right - legendWidth) / 2;
        startY = chartArea.top - legendHeight - padding;
    }

    // Ensure legend stays within bounds
    startX = Math.max(padding, startX);
    startY = Math.max(padding, startY);

    // Draw legend items
    datasets.forEach((dataset, i) => {
      const row = Math.floor(i / itemsPerRow);
      const col = i % itemsPerRow;
      const x = startX + col * itemWidth;
      const y = startY + row * itemHeight;

      // Draw color box
      this.ctx!.fillStyle = dataset.color || this.getDefaultColor(i);
      this.ctx!.fillRect(x, y + 4, 12, 12);

      // Draw border around color box
      this.ctx!.strokeStyle = options.theme === 'dark' ? this.theme.dark.axisColor : this.theme.light.axisColor;
      this.ctx!.lineWidth = 1;
      this.ctx!.strokeRect(x, y + 4, 12, 12);

      // Draw text
      this.ctx!.fillStyle = options.theme === 'dark' ? this.theme.dark.textColor : this.theme.light.textColor;
      this.ctx!.font = '12px Arial';
      this.ctx!.textAlign = 'left';
      this.ctx!.textBaseline = 'middle';
      this.ctx!.fillText(dataset.label || `Series ${i + 1}`, x + 18, y + 10);
    });
  }

  /**
   * Set up tooltip for chart
   */
  private setupTooltip(container: HTMLElement, data: ChartData, options: ChartOptions): void {
    if (!this.canvas) return;

    // Remove existing tooltip if any
    if (this.tooltipElement) {
      this.tooltipElement.remove();
    }

    // Create tooltip element
    this.tooltipElement = document.createElement('div');
    this.tooltipElement.className = 'chart-tooltip';
    this.tooltipElement.style.position = 'absolute';
    this.tooltipElement.style.display = 'none';
    this.tooltipElement.style.backgroundColor = options.theme === 'dark' ? '#333' : '#fff';
    this.tooltipElement.style.color = options.theme === 'dark' ? '#fff' : '#333';
    this.tooltipElement.style.border = `1px solid ${options.theme === 'dark' ? '#555' : '#ddd'}`;
    this.tooltipElement.style.padding = '8px';
    this.tooltipElement.style.borderRadius = '4px';
    this.tooltipElement.style.pointerEvents = 'none';
    this.tooltipElement.style.zIndex = '1000';
    this.tooltipElement.style.fontSize = '12px';
    this.tooltipElement.style.boxShadow = '0 2px 5px rgba(0,0,0,0.1)';

    container.appendChild(this.tooltipElement);

    // Add event listeners
    this.canvas.addEventListener('mousemove', (e) => this.handleMouseMove(e, data, options, container));
    this.canvas.addEventListener('mouseout', () => {
      if (this.tooltipElement) {
        this.tooltipElement.style.display = 'none';
      }
    });
  }

  /**
   * Handle mouse move for tooltips
   */
  private handleMouseMove(
    e: MouseEvent,
    data: ChartData,
    options: ChartOptions,
    container: HTMLElement
  ): void {
    if (!this.canvas || !this.ctx || !this.tooltipElement) return;

    const rect = this.canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Get chart area with padding
    const padding = options.padding || {};
    const chartArea = {
      left: padding.left || 0,
      right: this.containerWidth - (padding.right || 0),
      top: padding.top || 0,
      bottom: this.containerHeight - (padding.bottom || 0)
    };

    // Only process if mouse is inside chart area
    if (
      x < chartArea.left ||
      x > chartArea.right ||
      y < chartArea.top ||
      y > chartArea.bottom
    ) {
      this.tooltipElement.style.display = 'none';
      return;
    }

    // Find nearest data point(s)
    const nearestPoints = this.findNearestPoints(x, y, data, options, chartArea);

    if (nearestPoints.length === 0) {
      this.tooltipElement.style.display = 'none';
      return;
    }

    // Build tooltip content
    let tooltipContent = '';

    nearestPoints.forEach(({ dataset, point, dataIndex }) => {
      const formattedX = point.x instanceof Date 
        ? point.x.toLocaleDateString() 
        : point.x;

      const formattedY = typeof point.y === 'number' 
        ? point.y.toLocaleString(undefined, { maximumFractionDigits: 2 }) 
        : point.y;

      if (options.tooltip?.format) {
        tooltipContent += options.tooltip.format(point, dataset);
      } else {
        tooltipContent += `
          <div style="margin-bottom: 4px">
            <span style="font-weight: bold; color: ${dataset.color || this.getDefaultColor(dataIndex)}">${dataset.label || `Series ${dataIndex + 1}`}:</span>
            <span>${formattedX}, ${formattedY}</span>
          </div>
        `;
      }
    });

    // Update tooltip
    this.tooltipElement.innerHTML = tooltipContent;
    this.tooltipElement.style.display = 'block';

    // Position tooltip
    const tooltipRect = this.tooltipElement.getBoundingClientRect();
    let tooltipX = e.clientX - rect.left + container.scrollLeft + 10;
    let tooltipY = e.clientY - rect.top + container.scrollTop + 10;

    // Adjust position to ensure tooltip stays within container
    if (tooltipX + tooltipRect.width > container.clientWidth) {
      tooltipX = e.clientX - rect.left - tooltipRect.width - 10;
    }

    if (tooltipY + tooltipRect.height > container.clientHeight) {
      tooltipY = e.clientY - rect.top - tooltipRect.height - 10;
    }

    this.tooltipElement.style.left = `${tooltipX}px`;
    this.tooltipElement.style.top = `${tooltipY}px`;
  }

  /**
   * Find nearest data points to mouse position
   */
  private findNearestPoints(
    mouseX: number,
    mouseY: number,
    data: ChartData,
    options: ChartOptions,
    chartArea: any
  ): Array<{ dataset: ChartData['datasets'][0]; point: ChartDataPoint; dataIndex: number }> {
    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);
    const tooltipMode = options.tooltip?.mode || 'nearest';
    const intersect = options.tooltip?.intersect !== false;

    const nearestPoints: Array<{
      dataset: ChartData['datasets'][0];
      point: ChartDataPoint;
      dataIndex: number;
      distance: number;
    }> = [];

    // Find nearest points based on chart type and tooltip mode
    datasets.forEach((dataset, datasetIndex) => {
      dataset.data.forEach(point => {
        const pointX = this.mapValueToPixel(point.x, scales.x, chartArea.left, chartArea.right);
        const pointY = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);

        // Calculate distance
        const distance = Math.sqrt(Math.pow(mouseX - pointX, 2) + Math.pow(mouseY - pointY, 2));

        nearestPoints.push({
          dataset,
          point,
          dataIndex: datasetIndex,
          distance
        });
      });
    });

    // Sort by distance
    nearestPoints.sort((a, b) => a.distance - b.distance);

    // Filter based on mode
    let result: typeof nearestPoints = [];

    if (tooltipMode === 'nearest') {
      // Only include the nearest point
      if (nearestPoints.length > 0) {
        if (!intersect || nearestPoints[0].distance < 20) {
          result = [nearestPoints[0]];
        }
      }
    } else if (tooltipMode === 'point') {
      // Include all points within a threshold distance
      result = nearestPoints.filter(item => {
        return !intersect || item.distance < 20;
      });
    } else if (tooltipMode === 'dataset') {
      // Include the nearest point from each dataset
      const datasetMap = new Map<number, typeof nearestPoints[0]>();

      for (const item of nearestPoints) {
        if (!datasetMap.has(item.dataIndex) || 
            item.distance < datasetMap.get(item.dataIndex)!.distance) {
          datasetMap.set(item.dataIndex, item);
        }
      }

      result = Array.from(datasetMap.values()).filter(item => !intersect || item.distance < 40);
    }

    // Return without the distance property
    return result.map(({ dataset, point, dataIndex }) => ({ dataset, point, dataIndex }));
  }

  /**
   * Calculate scales for axes
   */
  private calculateScales(
    data: ChartData,
    chartArea: any,
    xAxis: ChartOptions['axes']['x'],
    yAxis: ChartOptions['axes']['y']
  ): { x: { min: number, max: number }, y: { min: number, max: number } } {
    const { datasets } = data;

    // Find min and max x values
    let xMin = xAxis?.min !== undefined ? Number(xAxis.min) : Infinity;
    let xMax = xAxis?.max !== undefined ? Number(xAxis.max) : -Infinity;

    // Find min and max y values
    let yMin = yAxis?.min !== undefined ? yAxis.min : Infinity;
    let yMax = yAxis?.max !== undefined ? yAxis.max : -Infinity;

    datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        // Handle x values
        let xValue = point.x;
        if (typeof xValue === 'string' && xAxis?.type === 'category') {
          // For category axes, use index
          xValue = 0; // Will be handled later
        } else if (xValue instanceof Date && xAxis?.type === 'time') {
          xValue = xValue.getTime();
        } else if (typeof xValue === 'string') {
          xValue = parseFloat(xValue);
        }

        if (typeof xValue === 'number' && !isNaN(xValue)) {
          xMin = Math.min(xMin, xValue);
          xMax = Math.max(xMax, xValue);
        }

        // Handle y values
        if (typeof point.y === 'number') {
          yMin = Math.min(yMin, point.y);
          yMax = Math.max(yMax, point.y);
        }
      });
    });

    // Handle case where all values are the same
    if (xMin === xMax) {
      xMin -= 1;
      xMax += 1;
    }

    if (yMin === yMax) {
      yMin = yMin === 0 ? -1 : yMin * 0.9;
      yMax = yMax === 0 ? 1 : yMax * 1.1;
    }

    // For Y axis, typically we want to include zero if it's nearby
    if (yAxis?.type === 'linear' && yMin > 0 && yMin < yMax * 0.3) {
      yMin = 0;
    }

    if (yAxis?.type === 'linear' && yMax < 0 && yMax > yMin * 0.3) {
      yMax = 0;
    }

    // Add some padding
    const xRange = xMax - xMin;
    const yRange = yMax - yMin;

    xMin -= xRange * 0.05;
    xMax += xRange * 0.05;
    yMin -= yRange * 0.05;
    yMax += yRange * 0.05;

    return {
      x: { min: xMin, max: xMax },
      y: { min: yMin, max: yMax }
    };
  }

  /**
   * Map a data value to a pixel position
   */
  private mapValueToPixel(
    value: any,
    scale: { min: number; max: number },
    pixelMin: number,
    pixelMax: number,
    invert = false
  ): number {
    let normalizedValue: number;

    if (value instanceof Date) {
      normalizedValue = value.getTime();
    } else if (typeof value === 'string') {
      normalizedValue = parseFloat(value);
      if (isNaN(normalizedValue)) {
        normalizedValue = 0; // Fallback for categories
      }
    } else {
      normalizedValue = Number(value);
    }

    const normalizedPosition = (normalizedValue - scale.min) / (scale.max - scale.min);
    return invert
      ? pixelMax - normalizedPosition * (pixelMax - pixelMin)
      : pixelMin + normalizedPosition * (pixelMax - pixelMin);
  }

  /**
   * Get all unique x values from all datasets
   */
  private getAllXValues(data: ChartData): string[] {
    const uniqueValues = new Set<string>();

    data.datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        uniqueValues.add(String(point.x));
      });
    });

    return Array.from(uniqueValues);
  }

  /**
   * Get a default color based on index
   */
  private getDefaultColor(index: number): string {
    const colors = [
      '#4e79a7',
      '#f28e2c',
      '#e15759',
      '#76b7b2',
      '#59a14f',
      '#edc949',
      '#af7aa1',
      '#ff9da7',
      '#9c755f',
      '#bab0ab'
    ];

    return colors[index % colors.length];
  }

  /**
   * Convert hex color to rgba for transparency
   */
  private hexToRgba(hex: string, alpha: number): string {
    // Handle shorthand hex
    const shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i;
    hex = hex.replace(shorthandRegex, (m, r, g, b) => r + r + g + g + b + b);

    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);

    if (!result) {
      return `rgba(0, 0, 0, ${alpha})`;
    }

    return `rgba(${parseInt(result[1], 16)}, ${parseInt(result[2], 16)}, ${parseInt(result[3], 16)}, ${alpha})`;
  }

  /**
   * Get color for heatmap based on normalized value
   */
  private getHeatmapColor(normalizedValue: number): string {
    // Use a multi-stop gradient: blue -> cyan -> green -> yellow -> red
    const stops = [
      { value: 0.0, color: [0, 0, 255] },      // blue
      { value: 0.25, color: [0, 255, 255] },   // cyan
      { value: 0.5, color: [0, 255, 0] },      // green
      { value: 0.75, color: [255, 255, 0] },   // yellow
      { value: 1.0, color: [255, 0, 0] }       // red
    ];

    // Find the two stops that our value is between
    let i = 0;
    while (i < stops.length - 1 && normalizedValue > stops[i + 1].value) {
      i++;
    }

    if (i === stops.length - 1) {
      const [r, g, b] = stops[i].color;
      return `rgb(${r}, ${g}, ${b})`;
    }

    // Interpolate between the two stops
    const lowerStop = stops[i];
    const upperStop = stops[i + 1];
    const range = upperStop.value - lowerStop.value;
    const relativePosition = (normalizedValue - lowerStop.value) / range;

    const r = Math.round(lowerStop.color[0] + relativePosition * (upperStop.color[0] - lowerStop.color[0]));
    const g = Math.round(lowerStop.color[1] + relativePosition * (upperStop.color[1] - lowerStop.color[1]));
    const b = Math.round(lowerStop.color[2] + relativePosition * (upperStop.color[2] - lowerStop.color[2]));

    return `rgb(${r}, ${g}, ${b})`;
  }
}

import { ChartData, ChartOptions, ChartRenderer, ChartType } from '../Chart';

/**
 * SVG-based chart renderer implementation.
 * Uses SVG for high-quality, interactive charts with moderate datasets.
 */
export class SVGRenderer implements ChartRenderer {
  private svg: SVGSVGElement | null = null;
  private container: HTMLElement | null = null;
  private containerWidth = 0;
  private containerHeight = 0;
  private isInitialized = false;
  private lastRenderTime = 0;
  private tooltipElement: HTMLDivElement | null = null;
  private interactiveElements: SVGElement[] = [];

  private theme = {
    light: {
      textColor: '#333333',
      gridColor: '#e0e0e0',
      axisColor: '#666666',
      backgroundColor: 'transparent'
    },
    dark: {
      textColor: '#e0e0e0',
      gridColor: '#444444',
      axisColor: '#999999',
      backgroundColor: 'transparent'
    }
  };

  /**
   * Render a chart onto the container element
   */
  public render(
    container: HTMLElement,
    data: ChartData,
    options: ChartOptions,
    type: ChartType
  ): void {
    this.initialize(container, options);
    this.update(container, data, options, type);
  }

  /**
   * Updates the chart with new data or options
   */
  public update(
    container: HTMLElement,
    data: ChartData,
    options: ChartOptions,
    type: ChartType
  ): void {
    if (!this.isInitialized) {
      this.initialize(container, options);
    }

    const startTime = performance.now();

    // Update dimensions if needed
    this.updateDimensions(container);

    // Clear previous content
    while (this.svg?.firstChild) {
      this.svg.removeChild(this.svg.firstChild);
    }
    this.interactiveElements = [];

    // Set background color
    const themeColors = options.theme === 'dark' ? this.theme.dark : this.theme.light;
    if (options.backgroundColor && options.backgroundColor !== 'transparent') {
      const background = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
      background.setAttribute('width', '100%');
      background.setAttribute('height', '100%');
      background.setAttribute('fill', options.backgroundColor);
      this.svg?.appendChild(background);
    }

    // Calculate chart area with padding
    const padding = options.padding || {};
    const chartArea = {
      left: padding.left || 0,
      right: this.containerWidth - (padding.right || 0),
      top: padding.top || 0,
      bottom: this.containerHeight - (padding.bottom || 0)
    };

    // Create a group for the chart area
    const chartGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    chartGroup.setAttribute('class', 'chart-area');
    this.svg?.appendChild(chartGroup);

    // Render the chart based on type
    switch (type) {
      case 'line':
        this.renderLineChart(data, options, chartArea, chartGroup);
        break;
      case 'bar':
        this.renderBarChart(data, options, chartArea, chartGroup);
        break;
      case 'scatter':
        this.renderScatterChart(data, options, chartArea, chartGroup);
        break;
      case 'area':
        this.renderAreaChart(data, options, chartArea, chartGroup);
        break;
      case 'pie':
        this.renderPieChart(data, options, chartGroup);
        break;
      case 'radar':
        this.renderRadarChart(data, options, chartGroup);
        break;
      case 'heatmap':
        this.renderHeatmapChart(data, options, chartArea, chartGroup);
        break;
      default:
        throw new Error(`Unsupported chart type: ${type}`);
    }

    // Render axes if needed
    if (type !== 'pie' && type !== 'radar') {
      this.renderAxes(data, options, chartArea, chartGroup);
    }

    // Render legend if enabled
    if (options.legend?.visible) {
      this.renderLegend(data, options, chartArea);
    }

    // Setup tooltips if enabled
    if (options.tooltip?.enabled) {
      this.setupTooltip(container, data, options);
    } else if (this.tooltipElement) {
      this.tooltipElement.remove();
      this.tooltipElement = null;
    }

    this.lastRenderTime = performance.now() - startTime;
  }

  /**
   * Destroys the renderer, cleaning up any resources
   */
  public destroy(): void {
    if (this.svg && this.container) {
      this.container.removeChild(this.svg);
    }

    if (this.tooltipElement) {
      this.tooltipElement.remove();
    }

    this.svg = null;
    this.container = null;
    this.tooltipElement = null;
    this.interactiveElements = [];
    this.isInitialized = false;
  }

  /**
   * Returns the current status of the renderer
   */
  public getStatus(): { isInitialized: boolean; lastRenderTime?: number } {
    return {
      isInitialized: this.isInitialized,
      lastRenderTime: this.lastRenderTime
    };
  }

  /**
   * Initialize the SVG renderer
   */
  private initialize(container: HTMLElement, options: ChartOptions): void {
    // Clean existing SVG if any
    const existingSvg = container.querySelector('svg');
    if (existingSvg) {
      container.removeChild(existingSvg);
    }

    // Create new SVG
    this.svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
    this.svg.setAttribute('xmlns', 'http://www.w3.org/2000/svg');
    this.svg.style.display = 'block';
    this.svg.style.width = '100%';
    this.svg.style.height = '100%';

    container.appendChild(this.svg);
    this.container = container;

    // Set dimensions
    this.updateDimensions(container);

    this.isInitialized = true;
  }

  /**
   * Update SVG dimensions based on container size
   */
  private updateDimensions(container: HTMLElement): void {
    if (!this.svg) return;

    const rect = container.getBoundingClientRect();
    this.containerWidth = rect.width;
    this.containerHeight = rect.height;

    this.svg.setAttribute('width', this.containerWidth.toString());
    this.svg.setAttribute('height', this.containerHeight.toString());
    this.svg.setAttribute('viewBox', `0 0 ${this.containerWidth} ${this.containerHeight}`);
  }

  /**
   * Render line chart
   */
  private renderLineChart(
    data: ChartData,
    options: ChartOptions,
    chartArea: any,
    chartGroup: SVGGElement
  ): void {
    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };

    // Calculate scales
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);

    // Draw grid lines if enabled
    if (xAxis.grid || yAxis.grid) {
      this.drawGrid(scales, chartArea, options, chartGroup);
    }

    // Create a group for each dataset
    datasets.forEach((dataset, datasetIndex) => {
      const datasetGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
      datasetGroup.setAttribute('class', `dataset-${datasetIndex}`);
      chartGroup.appendChild(datasetGroup);

      const points: { x: number; y: number }[] = [];

      // Map data points to SVG coordinates
      dataset.data.forEach(point => {
        const x = this.mapValueToPixel(point.x, scales.x, chartArea.left, chartArea.right);
        const y = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);
        points.push({ x, y });
      });

      if (points.length === 0) return;

      // Create the line path
      const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
      let pathData = `M ${points[0].x} ${points[0].y}`;

      for (let i = 1; i < points.length; i++) {
        pathData += ` L ${points[i].x} ${points[i].y}`;
      }

      path.setAttribute('d', pathData);
      path.setAttribute('fill', 'none');
      path.setAttribute('stroke', dataset.color || this.getDefaultColor(datasetIndex));
      path.setAttribute('stroke-width', '2');
      datasetGroup.appendChild(path);

      // Draw data points
      points.forEach((point, i) => {
        const circle = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
        circle.setAttribute('cx', point.x.toString());
        circle.setAttribute('cy', point.y.toString());
        circle.setAttribute('r', '3');
        circle.setAttribute('fill', dataset.color || this.getDefaultColor(datasetIndex));

        // Store original data for tooltips
        circle.dataset.index = i.toString();
        circle.dataset.datasetIndex = datasetIndex.toString();
        circle.dataset.x = dataset.data[i].x.toString();
        circle.dataset.y = dataset.data[i].y.toString();

        this.interactiveElements.push(circle);
        datasetGroup.appendChild(circle);
      });
    });
  }

  /**
   * Render bar chart
   */
  private renderBarChart(
    data: ChartData,
    options: ChartOptions,
    chartArea: any,
    chartGroup: SVGGElement
  ): void {
    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'category' };
    const yAxis = options.axes?.y || { type: 'linear' };

    // Calculate scales
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);

    // Draw grid lines if enabled
    if (xAxis.grid || yAxis.grid) {
      this.drawGrid(scales, chartArea, options, chartGroup);
    }

    // Calculate bar width based on the number of datasets and data points
    const allLabels = this.getAllXValues(data);
    const totalBarGroups = allLabels.length;
    const barGroupWidth = (chartArea.right - chartArea.left) / (totalBarGroups + 1);
    const barWidth = barGroupWidth * 0.8 / datasets.length;

    // Draw each dataset
    datasets.forEach((dataset, datasetIndex) => {
      const datasetGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
      datasetGroup.setAttribute('class', `dataset-${datasetIndex}`);
      chartGroup.appendChild(datasetGroup);

      dataset.data.forEach((point, i) => {
        const xIndex = allLabels.indexOf(String(point.x));
        const x = chartArea.left + (xIndex + 1) * barGroupWidth - barGroupWidth * 0.4 + datasetIndex * barWidth;
        const yZero = this.mapValueToPixel(0, scales.y, chartArea.bottom, chartArea.top, true);
        const y = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);

        const rect = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
        rect.setAttribute('x', x.toString());
        rect.setAttribute('y', Math.min(y, yZero).toString());
        rect.setAttribute('width', barWidth.toString());
        rect.setAttribute('height', Math.abs(y - yZero).toString());
        rect.setAttribute('fill', dataset.color || this.getDefaultColor(datasetIndex));

        // Store original data for tooltips
        rect.dataset.index = i.toString();
        rect.dataset.datasetIndex = datasetIndex.toString();
        rect.dataset.x = point.x.toString();
        rect.dataset.y = point.y.toString();

        this.interactiveElements.push(rect);
        datasetGroup.appendChild(rect);
      });
    });
  }

  /**
   * Render scatter chart
   */
  private renderScatterChart(
    data: ChartData,
    options: ChartOptions,
    chartArea: any,
    chartGroup: SVGGElement
  ): void {
    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };

    // Calculate scales
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);

    // Draw grid lines if enabled
    if (xAxis.grid || yAxis.grid) {
      this.drawGrid(scales, chartArea, options, chartGroup);
    }

    // Draw each dataset
    datasets.forEach((dataset, datasetIndex) => {
      const datasetGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
      datasetGroup.setAttribute('class', `dataset-${datasetIndex}`);
      chartGroup.appendChild(datasetGroup);

      dataset.data.forEach((point, i) => {
        const x = this.mapValueToPixel(point.x, scales.x, chartArea.left, chartArea.right);
        const y = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);
        const radius = point.radius || 5;

        const circle = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
        circle.setAttribute('cx', x.toString());
        circle.setAttribute('cy', y.toString());
        circle.setAttribute('r', radius.toString());
        circle.setAttribute('fill', dataset.color || this.getDefaultColor(datasetIndex));

        // Store original data for tooltips
        circle.dataset.index = i.toString();
        circle.dataset.datasetIndex = datasetIndex.toString();
        circle.dataset.x = point.x.toString();
        circle.dataset.y = point.y.toString();

        this.interactiveElements.push(circle);
        datasetGroup.appendChild(circle);
      });
    });
  }

  /**
   * Render area chart
   */
  private renderAreaChart(
    data: ChartData,
    options: ChartOptions,
    chartArea: any,
    chartGroup: SVGGElement
  ): void {
    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };

    // Calculate scales
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);

    // Draw grid lines if enabled
    if (xAxis.grid || yAxis.grid) {
      this.drawGrid(scales, chartArea, options, chartGroup);
    }

    // Draw each dataset
    datasets.forEach((dataset, datasetIndex) => {
      const datasetGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
      datasetGroup.setAttribute('class', `dataset-${datasetIndex}`);
      chartGroup.appendChild(datasetGroup);

      const points: { x: number; y: number }[] = [];

      // Map data points to SVG coordinates
      dataset.data.forEach(point => {
        const x = this.mapValueToPixel(point.x, scales.x, chartArea.left, chartArea.right);
        const y = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);
        points.push({ x, y });
      });

      if (points.length === 0) return;

      // Create the area path
      const areaPath = document.createElementNS('http://www.w3.org/2000/svg', 'path');
      let pathData = `M ${points[0].x} ${chartArea.bottom}`;
      pathData += ` L ${points[0].x} ${points[0].y}`;

      for (let i = 1; i < points.length; i++) {
        pathData += ` L ${points[i].x} ${points[i].y}`;
      }

      pathData += ` L ${points[points.length - 1].x} ${chartArea.bottom} Z`;

      areaPath.setAttribute('d', pathData);

      // Create gradient for area
      const color = dataset.color || this.getDefaultColor(datasetIndex);
      const gradientId = `gradient-${datasetIndex}`;
      const gradient = document.createElementNS('http://www.w3.org/2000/svg', 'linearGradient');
      gradient.setAttribute('id', gradientId);
      gradient.setAttribute('x1', '0');
      gradient.setAttribute('y1', '0');
      gradient.setAttribute('x2', '0');
      gradient.setAttribute('y2', '1');

      const stop1 = document.createElementNS('http://www.w3.org/2000/svg', 'stop');
      stop1.setAttribute('offset', '0%');
      stop1.setAttribute('stop-color', color);
      stop1.setAttribute('stop-opacity', '0.6');

      const stop2 = document.createElementNS('http://www.w3.org/2000/svg', 'stop');
      stop2.setAttribute('offset', '100%');
      stop2.setAttribute('stop-color', color);
      stop2.setAttribute('stop-opacity', '0.1');

      gradient.appendChild(stop1);
      gradient.appendChild(stop2);

      // Add gradient to defs
      const defs = document.createElementNS('http://www.w3.org/2000/svg', 'defs');
      defs.appendChild(gradient);
      datasetGroup.appendChild(defs);

      areaPath.setAttribute('fill', `url(#${gradientId})`);
      datasetGroup.appendChild(areaPath);

      // Create the line path
      const linePath = document.createElementNS('http://www.w3.org/2000/svg', 'path');
      let linePathData = `M ${points[0].x} ${points[0].y}`;

      for (let i = 1; i < points.length; i++) {
        linePathData += ` L ${points[i].x} ${points[i].y}`;
      }

      linePath.setAttribute('d', linePathData);
      linePath.setAttribute('fill', 'none');
      linePath.setAttribute('stroke', color);
      linePath.setAttribute('stroke-width', '2');
      datasetGroup.appendChild(linePath);

      // Draw data points
      points.forEach((point, i) => {
        const circle = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
        circle.setAttribute('cx', point.x.toString());
        circle.setAttribute('cy', point.y.toString());
        circle.setAttribute('r', '3');
        circle.setAttribute('fill', color);

        // Store original data for tooltips
        circle.dataset.index = i.toString();
        circle.dataset.datasetIndex = datasetIndex.toString();
        circle.dataset.x = dataset.data[i].x.toString();
        circle.dataset.y = dataset.data[i].y.toString();

        this.interactiveElements.push(circle);
        datasetGroup.appendChild(circle);
      });
    });
  }

  /**
   * Render pie chart
   */
  private renderPieChart(
    data: ChartData,
    options: ChartOptions,
    chartGroup: SVGGElement
  ): void {
    const { datasets } = data;

    const centerX = this.containerWidth / 2;
    const centerY = this.containerHeight / 2;
    const radius = Math.min(centerX, centerY) * 0.7;

    if (datasets.length === 0 || datasets[0].data.length === 0) return;

    // Get the first dataset for pie chart
    const dataset = datasets[0];

    // Calculate total value for proportions
    const total = dataset.data.reduce((sum, point) => sum + point.y, 0);
    if (total <= 0) return;

    // Create a group for the pie
    const pieGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    pieGroup.setAttribute('class', 'pie');
    chartGroup.appendChild(pieGroup);

    // Draw pie slices
    let startAngle = 0;
    dataset.data.forEach((point, i) => {
      const sliceAngle = (point.y / total) * (Math.PI * 2);
      const endAngle = startAngle + sliceAngle;

      // Calculate SVG arc path
      const x1 = centerX + Math.cos(startAngle) * radius;
      const y1 = centerY + Math.sin(startAngle) * radius;
      const x2 = centerX + Math.cos(endAngle) * radius;
      const y2 = centerY + Math.sin(endAngle) * radius;

      // Use largeArcFlag (0/1) to determine if arc is > 180 degrees
      const largeArcFlag = sliceAngle > Math.PI ? 1 : 0;

      // Create pie slice
      const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
      const pathData = `M ${centerX} ${centerY} L ${x1} ${y1} A ${radius} ${radius} 0 ${largeArcFlag} 1 ${x2} ${y2} Z`;
      path.setAttribute('d', pathData);
      path.setAttribute('fill', point.color || this.getDefaultColor(i));
      path.setAttribute('stroke', 'white');
      path.setAttribute('stroke-width', '1');

      // Store original data for tooltips
      path.dataset.index = i.toString();
      path.dataset.datasetIndex = '0';
      path.dataset.x = point.x.toString();
      path.dataset.y = point.y.toString();
      path.dataset.percentage = ((point.y / total) * 100).toFixed(1);

      this.interactiveElements.push(path);
      pieGroup.appendChild(path);

      // Draw label if there's enough space (slices > 5% of total)
      if (sliceAngle > 0.1) {
        const labelAngle = startAngle + sliceAngle / 2;
        const labelRadius = radius * 0.7;
        const labelX = centerX + Math.cos(labelAngle) * labelRadius;
        const labelY = centerY + Math.sin(labelAngle) * labelRadius;

        const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
        text.setAttribute('x', labelX.toString());
        text.setAttribute('y', labelY.toString());
        text.setAttribute('text-anchor', 'middle');
        text.setAttribute('dominant-baseline', 'middle');
        text.setAttribute('fill', 'white');
        text.setAttribute('font-size', '12px');
        text.textContent = point.x.toString();

        pieGroup.appendChild(text);
      }

      startAngle = endAngle;
    });
  }

  /**
   * Render radar chart
   */
  private renderRadarChart(
    data: ChartData,
    options: ChartOptions,
    chartGroup: SVGGElement
  ): void {
    const { datasets } = data;

    const centerX = this.containerWidth / 2;
    const centerY = this.containerHeight / 2;
    const radius = Math.min(centerX, centerY) * 0.7;

    if (datasets.length === 0 || datasets[0].data.length === 0) return;

    // Find all unique labels (angles) from all datasets
    const allLabels = new Set<string>();
    datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        allLabels.add(String(point.x));
      });
    });

    const labels = Array.from(allLabels);
    const angleStep = (Math.PI * 2) / labels.length;

    // Create a group for the radar chart
    const radarGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    radarGroup.setAttribute('class', 'radar');
    chartGroup.appendChild(radarGroup);

    // Draw background grid (circles and lines)
    const gridGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    gridGroup.setAttribute('class', 'grid');
    radarGroup.appendChild(gridGroup);

    const themeColors = options.theme === 'dark' ? this.theme.dark : this.theme.light;

    // Draw circular grid lines
    for (let i = 1; i <= 5; i++) {
      const gridRadius = radius * (i / 5);
      const circle = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
      circle.setAttribute('cx', centerX.toString());
      circle.setAttribute('cy', centerY.toString());
      circle.setAttribute('r', gridRadius.toString());
      circle.setAttribute('fill', 'none');
      circle.setAttribute('stroke', themeColors.gridColor);
      circle.setAttribute('stroke-width', '0.5');
      gridGroup.appendChild(circle);
    }

    // Draw radial grid lines and labels
    labels.forEach((label, i) => {
      const angle = i * angleStep - Math.PI / 2; // Start from top

      // Draw grid line
      const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
      line.setAttribute('x1', centerX.toString());
      line.setAttribute('y1', centerY.toString());
      line.setAttribute('x2', (centerX + Math.cos(angle) * radius).toString());
      line.setAttribute('y2', (centerY + Math.sin(angle) * radius).toString());
      line.setAttribute('stroke', themeColors.gridColor);
      line.setAttribute('stroke-width', '0.5');
      gridGroup.appendChild(line);

      // Draw label
      const labelX = centerX + Math.cos(angle) * (radius + 15);
      const labelY = centerY + Math.sin(angle) * (radius + 15);

      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', labelX.toString());
      text.setAttribute('y', labelY.toString());
      text.setAttribute('text-anchor', 'middle');
      text.setAttribute('dominant-baseline', 'middle');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '12px');
      text.textContent = label;

      gridGroup.appendChild(text);
    });

    // Find max value for scaling
    const maxValue = datasets.reduce((max, dataset) => {
      const datasetMax = dataset.data.reduce((m, point) => Math.max(m, point.y), 0);
      return Math.max(max, datasetMax);
    }, 0);

    // Draw each dataset
    datasets.forEach((dataset, datasetIndex) => {
      const datasetGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
      datasetGroup.setAttribute('class', `dataset-${datasetIndex}`);
      radarGroup.appendChild(datasetGroup);

      const dataMap = new Map<string, number>();
      dataset.data.forEach(point => {
        dataMap.set(String(point.x), point.y);
      });

      // Create points for the radar shape
      const points: { x: number; y: number }[] = [];
      labels.forEach((label, i) => {
        const value = dataMap.get(label) || 0;
        const valueRatio = Math.max(0, Math.min(1, value / maxValue));
        const angle = i * angleStep - Math.PI / 2; // Start from top
        const x = centerX + Math.cos(angle) * radius * valueRatio;
        const y = centerY + Math.sin(angle) * radius * valueRatio;
        points.push({ x, y });
      });

      if (points.length < 3) return;

      // Create the radar shape
      const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
      let pathData = `M ${points[0].x} ${points[0].y}`;

      for (let i = 1; i < points.length; i++) {
        pathData += ` L ${points[i].x} ${points[i].y}`;
      }

      pathData += ' Z'; // Close the path

      path.setAttribute('d', pathData);

      const color = dataset.color || this.getDefaultColor(datasetIndex);
      path.setAttribute('fill', this.hexToRgba(color, 0.2));
      path.setAttribute('stroke', color);
      path.setAttribute('stroke-width', '2');

      datasetGroup.appendChild(path);

      // Draw points
      points.forEach((point, i) => {
        const circle = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
        circle.setAttribute('cx', point.x.toString());
        circle.setAttribute('cy', point.y.toString());
        circle.setAttribute('r', '3');
        circle.setAttribute('fill', color);

        // Store original data for tooltips
        const originalValue = dataMap.get(labels[i]) || 0;
        circle.dataset.index = i.toString();
        circle.dataset.datasetIndex = datasetIndex.toString();
        circle.dataset.x = labels[i];
        circle.dataset.y = originalValue.toString();

        this.interactiveElements.push(circle);
        datasetGroup.appendChild(circle);
      });
    });
  }

  /**
   * Render heatmap chart
   */
  private renderHeatmapChart(
    data: ChartData,
    options: ChartOptions,
    chartArea: any,
    chartGroup: SVGGElement
  ): void {
    const { datasets } = data;

    if (datasets.length === 0 || datasets[0].data.length === 0) return;

    // For heatmap, we need to find all unique x and y values
    const uniqueX = new Set<string>();
    const uniqueY = new Set<string>();

    datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        uniqueX.add(String(point.x));
        uniqueY.add(String(point.y));
      });
    });

    const xValues = Array.from(uniqueX).sort();
    const yValues = Array.from(uniqueY).sort();

    // Calculate cell dimensions
    const cellWidth = (chartArea.right - chartArea.left) / xValues.length;
    const cellHeight = (chartArea.bottom - chartArea.top) / yValues.length;

    // Find min and max values for color scaling
    let minValue = Infinity;
    let maxValue = -Infinity;

    datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        const value = point.value !== undefined ? point.value : point.y;
        minValue = Math.min(minValue, value);
        maxValue = Math.max(maxValue, value);
      });
    });

    // Create a group for the heatmap
    const heatmapGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    heatmapGroup.setAttribute('class', 'heatmap');
    chartGroup.appendChild(heatmapGroup);

    // Draw grid
    const themeColors = options.theme === 'dark' ? this.theme.dark : this.theme.light;
    const gridGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    gridGroup.setAttribute('class', 'grid');
    heatmapGroup.appendChild(gridGroup);

    // Vertical grid lines
    for (let i = 0; i <= xValues.length; i++) {
      const x = chartArea.left + i * cellWidth;
      const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
      line.setAttribute('x1', x.toString());
      line.setAttribute('y1', chartArea.top.toString());
      line.setAttribute('x2', x.toString());
      line.setAttribute('y2', chartArea.bottom.toString());
      line.setAttribute('stroke', themeColors.gridColor);
      line.setAttribute('stroke-width', '0.5');
      gridGroup.appendChild(line);
    }

    // Horizontal grid lines
    for (let i = 0; i <= yValues.length; i++) {
      const y = chartArea.top + i * cellHeight;
      const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
      line.setAttribute('x1', chartArea.left.toString());
      line.setAttribute('y1', y.toString());
      line.setAttribute('x2', chartArea.right.toString());
      line.setAttribute('y2', y.toString());
      line.setAttribute('stroke', themeColors.gridColor);
      line.setAttribute('stroke-width', '0.5');
      gridGroup.appendChild(line);
    }

    // Draw cells
    datasets.forEach((dataset, datasetIndex) => {
      dataset.data.forEach((point, i) => {
        const xIndex = xValues.indexOf(String(point.x));
        const yIndex = yValues.indexOf(String(point.y));

        if (xIndex === -1 || yIndex === -1) return;

        const value = point.value !== undefined ? point.value : point.y;
        const normalizedValue = (value - minValue) / (maxValue - minValue);

        const x = chartArea.left + xIndex * cellWidth;
        const y = chartArea.top + yIndex * cellHeight;

        const rect = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
        rect.setAttribute('x', x.toString());
        rect.setAttribute('y', y.toString());
        rect.setAttribute('width', cellWidth.toString());
        rect.setAttribute('height', cellHeight.toString());
        rect.setAttribute('fill', this.getHeatmapColor(normalizedValue));

        // Store original data for tooltips
        rect.dataset.index = i.toString();
        rect.dataset.datasetIndex = datasetIndex.toString();
        rect.dataset.x = point.x.toString();
        rect.dataset.y = point.y.toString();
        rect.dataset.value = value.toString();

        this.interactiveElements.push(rect);
        heatmapGroup.appendChild(rect);
      });
    });

    // Draw axes labels
    this.renderCategoryAxes(xValues, yValues, chartArea, options, chartGroup);
  }

  /**
   * Render axes for charts
   */
  private renderAxes(
    data: ChartData,
    options: ChartOptions,
    chartArea: any,
    chartGroup: SVGGElement
  ): void {
    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);
    const themeColors = options.theme === 'dark' ? this.theme.dark : this.theme.light;

    // Create a group for the axes
    const axesGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    axesGroup.setAttribute('class', 'axes');
    chartGroup.appendChild(axesGroup);

    // Draw x-axis
    const xAxisLine = document.createElementNS('http://www.w3.org/2000/svg', 'line');
    xAxisLine.setAttribute('x1', chartArea.left.toString());
    xAxisLine.setAttribute('y1', chartArea.bottom.toString());
    xAxisLine.setAttribute('x2', chartArea.right.toString());
    xAxisLine.setAttribute('y2', chartArea.bottom.toString());
    xAxisLine.setAttribute('stroke', themeColors.axisColor);
    xAxisLine.setAttribute('stroke-width', '1');
    axesGroup.appendChild(xAxisLine);

    // Draw y-axis
    const yAxisLine = document.createElementNS('http://www.w3.org/2000/svg', 'line');
    yAxisLine.setAttribute('x1', chartArea.left.toString());
    yAxisLine.setAttribute('y1', chartArea.top.toString());
    yAxisLine.setAttribute('x2', chartArea.left.toString());
    yAxisLine.setAttribute('y2', chartArea.bottom.toString());
    yAxisLine.setAttribute('stroke', themeColors.axisColor);
    yAxisLine.setAttribute('stroke-width', '1');
    axesGroup.appendChild(yAxisLine);

    // Draw x-axis ticks and labels
    const xTickCount = xAxis.tickCount || 5;
    const xStep = (scales.x.max - scales.x.min) / (xTickCount - 1);

    for (let i = 0; i < xTickCount; i++) {
      const value = scales.x.min + i * xStep;
      const x = this.mapValueToPixel(value, scales.x, chartArea.left, chartArea.right);
      const y = chartArea.bottom;

      // Draw tick
      const tick = document.createElementNS('http://www.w3.org/2000/svg', 'line');
      tick.setAttribute('x1', x.toString());
      tick.setAttribute('y1', y.toString());
      tick.setAttribute('x2', x.toString());
      tick.setAttribute('y2', (y + 5).toString());
      tick.setAttribute('stroke', themeColors.axisColor);
      tick.setAttribute('stroke-width', '1');
      axesGroup.appendChild(tick);

      // Draw label
      let tickLabel = String(value);
      if (xAxis.tickFormat) {
        tickLabel = xAxis.tickFormat(value);
      } else if (xAxis.type === 'time' && value instanceof Date) {
        tickLabel = value.toLocaleDateString();
      }

      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', x.toString());
      text.setAttribute('y', (y + 15).toString());
      text.setAttribute('text-anchor', 'middle');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '12px');
      text.textContent = tickLabel;

      axesGroup.appendChild(text);
    }

    // Draw y-axis ticks and labels
    const yTickCount = yAxis.tickCount || 5;
    const yStep = (scales.y.max - scales.y.min) / (yTickCount - 1);

    for (let i = 0; i < yTickCount; i++) {
      const value = scales.y.min + i * yStep;
      const x = chartArea.left;
      const y = this.mapValueToPixel(value, scales.y, chartArea.bottom, chartArea.top, true);

      // Draw tick
      const tick = document.createElementNS('http://www.w3.org/2000/svg', 'line');
      tick.setAttribute('x1', x.toString());
      tick.setAttribute('y1', y.toString());
      tick.setAttribute('x2', (x - 5).toString());
      tick.setAttribute('y2', y.toString());
      tick.setAttribute('stroke', themeColors.axisColor);
      tick.setAttribute('stroke-width', '1');
      axesGroup.appendChild(tick);

      // Draw label
      let tickLabel = String(value);
      if (yAxis.tickFormat) {
        tickLabel = yAxis.tickFormat(value);
      }

      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', (x - 8).toString());
      text.setAttribute('y', y.toString());
      text.setAttribute('text-anchor', 'end');
      text.setAttribute('dominant-baseline', 'middle');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '12px');
      text.textContent = tickLabel;

      axesGroup.appendChild(text);
    }

    // Draw axis labels if provided
    if (xAxis.label) {
      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', ((chartArea.left + chartArea.right) / 2).toString());
      text.setAttribute('y', (this.containerHeight - 5).toString());
      text.setAttribute('text-anchor', 'middle');
      text.setAttribute('dominant-baseline', 'bottom');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '14px');
      text.setAttribute('font-weight', 'bold');
      text.textContent = xAxis.label;

      axesGroup.appendChild(text);
    }

    if (yAxis.label) {
      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('transform', `translate(10, ${(chartArea.top + chartArea.bottom) / 2}) rotate(-90)`);
      text.setAttribute('text-anchor', 'middle');
      text.setAttribute('dominant-baseline', 'bottom');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '14px');
      text.setAttribute('font-weight', 'bold');
      text.textContent = yAxis.label;

      axesGroup.appendChild(text);
    }
  }

  /**
   * Render category axes for heatmap
   */
  private renderCategoryAxes(
    xCategories: string[],
    yCategories: string[],
    chartArea: any,
    options: ChartOptions,
    chartGroup: SVGGElement
  ): void {
    const cellWidth = (chartArea.right - chartArea.left) / xCategories.length;
    const cellHeight = (chartArea.bottom - chartArea.top) / yCategories.length;
    const themeColors = options.theme === 'dark' ? this.theme.dark : this.theme.light;

    // Create a group for the axes labels
    const labelsGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    labelsGroup.setAttribute('class', 'axis-labels');
    chartGroup.appendChild(labelsGroup);

    // X-axis labels
    xCategories.forEach((category, i) => {
      const x = chartArea.left + (i + 0.5) * cellWidth;
      const y = chartArea.bottom + 15;

      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', x.toString());
      text.setAttribute('y', y.toString());
      text.setAttribute('text-anchor', 'middle');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '12px');
      text.textContent = category;

      labelsGroup.appendChild(text);
    });

    // Y-axis labels
    yCategories.forEach((category, i) => {
      const x = chartArea.left - 8;
      const y = chartArea.top + (i + 0.5) * cellHeight;

      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', x.toString());
      text.setAttribute('y', y.toString());
      text.setAttribute('text-anchor', 'end');
      text.setAttribute('dominant-baseline', 'middle');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '12px');
      text.textContent = category;

      labelsGroup.appendChild(text);
    });
  }

  /**
   * Draw grid lines
   */
  private drawGrid(
    scales: any,
    chartArea: any,
    options: ChartOptions,
    chartGroup: SVGGElement
  ): void {
    const xAxis = options.axes?.x || { type: 'linear', grid: true };
    const yAxis = options.axes?.y || { type: 'linear', grid: true };
    const themeColors = options.theme === 'dark' ? this.theme.dark : this.theme.light;

    // Create a group for the grid
    const gridGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    gridGroup.setAttribute('class', 'grid');
    chartGroup.appendChild(gridGroup);

    // Draw x-axis grid lines
    if (yAxis.grid) {
      const xTickCount = xAxis.tickCount || 5;
      const xStep = (scales.x.max - scales.x.min) / (xTickCount - 1);

      for (let i = 0; i < xTickCount; i++) {
        const value = scales.x.min + i * xStep;
        const x = this.mapValueToPixel(value, scales.x, chartArea.left, chartArea.right);

        const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
        line.setAttribute('x1', x.toString());
        line.setAttribute('y1', chartArea.top.toString());
        line.setAttribute('x2', x.toString());
        line.setAttribute('y2', chartArea.bottom.toString());
        line.setAttribute('stroke', themeColors.gridColor);
        line.setAttribute('stroke-width', '0.5');

        gridGroup.appendChild(line);
      }
    }

    // Draw y-axis grid lines
    if (xAxis.grid) {
      const yTickCount = yAxis.tickCount || 5;
      const yStep = (scales.y.max - scales.y.min) / (yTickCount - 1);

      for (let i = 0; i < yTickCount; i++) {
        const value = scales.y.min + i * yStep;
        const y = this.mapValueToPixel(value, scales.y, chartArea.bottom, chartArea.top, true);

        const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
        line.setAttribute('x1', chartArea.left.toString());
        line.setAttribute('y1', y.toString());
        line.setAttribute('x2', chartArea.right.toString());
        line.setAttribute('y2', y.toString());
        line.setAttribute('stroke', themeColors.gridColor);
        line.setAttribute('stroke-width', '0.5');

        gridGroup.appendChild(line);
      }
    }
  }

  /**
   * Render legend
   */
  private renderLegend(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.svg) return;

    const { datasets } = data;
    const legendOptions = options.legend || { visible: true, position: 'top' };

    if (!legendOptions.visible || datasets.length === 0) return;

    const padding = 10;
    const itemHeight = 20;
    const itemWidth = 80;
    const itemsPerRow = Math.floor((chartArea.right - chartArea.left) / itemWidth);
    const rows = Math.ceil(datasets.length / itemsPerRow);

    let startX, startY, legendWidth, legendHeight;

    legendWidth = Math.min(datasets.length, itemsPerRow) * itemWidth;
    legendHeight = rows * itemHeight;

    switch (legendOptions.position) {
      case 'top':
        startX = (chartArea.left + chartArea.right - legendWidth) / 2;
        startY = chartArea.top - legendHeight - padding;
        break;
      case 'bottom':
        startX = (chartArea.left + chartArea.right - legendWidth) / 2;
        startY = chartArea.bottom + padding;
        break;
      case 'left':
        startX = chartArea.left - legendWidth - padding;
        startY = (chartArea.top + chartArea.bottom - legendHeight) / 2;
        break;
      case 'right':
        startX = chartArea.right + padding;
        startY = (chartArea.top + chartArea.bottom - legendHeight) / 2;
        break;
      default:
        startX = (chartArea.left + chartArea.right - legendWidth) / 2;
        startY = chartArea.top - legendHeight - padding;
    }

    // Ensure legend stays within bounds
    startX = Math.max(padding, startX);
    startY = Math.max(padding, startY);

    // Create legend group
    const legendGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    legendGroup.setAttribute('class', 'legend');
    this.svg.appendChild(legendGroup);

    const themeColors = options.theme === 'dark' ? this.theme.dark : this.theme.light;

    // Draw legend items
    datasets.forEach((dataset, i) => {
      const row = Math.floor(i / itemsPerRow);
      const col = i % itemsPerRow;
      const x = startX + col * itemWidth;
      const y = startY + row * itemHeight;

      // Draw color box
      const rect = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
      rect.setAttribute('x', x.toString());
      rect.setAttribute('y', (y + 4).toString());
      rect.setAttribute('width', '12');
      rect.setAttribute('height', '12');
      rect.setAttribute('fill', dataset.color || this.getDefaultColor(i));
      rect.setAttribute('stroke', themeColors.axisColor);
      rect.setAttribute('stroke-width', '1');

      legendGroup.appendChild(rect);

      // Draw text
      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', (x + 18).toString());
      text.setAttribute('y', (y + 10).toString());
      text.setAttribute('dominant-baseline', 'middle');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '12px');
      text.textContent = dataset.label || `Series ${i + 1}`;

      legendGroup.appendChild(text);
    });
  }

  /**
   * Set up tooltip for chart
   */
  private setupTooltip(container: HTMLElement, data: ChartData, options: ChartOptions): void {
    if (!this.svg) return;

    // Remove existing tooltip if any
    if (this.tooltipElement) {
      this.tooltipElement.remove();
    }

    // Create tooltip element
    this.tooltipElement = document.createElement('div');
    this.tooltipElement.className = 'chart-tooltip';
    this.tooltipElement.style.position = 'absolute';
    this.tooltipElement.style.display = 'none';
    this.tooltipElement.style.backgroundColor = options.theme === 'dark' ? '#333' : '#fff';
    this.tooltipElement.style.color = options.theme === 'dark' ? '#fff' : '#333';
    this.tooltipElement.style.border = `1px solid ${options.theme === 'dark' ? '#555' : '#ddd'}`;
    this.tooltipElement.style.padding = '8px';
    this.tooltipElement.style.borderRadius = '4px';
    this.tooltipElement.style.pointerEvents = 'none';
    this.tooltipElement.style.zIndex = '1000';
    this.tooltipElement.style.fontSize = '12px';
    this.tooltipElement.style.boxShadow = '0 2px 5px rgba(0,0,0,0.1)';

    container.appendChild(this.tooltipElement);

    // Add event listeners to interactive elements
    this.interactiveElements.forEach(element => {
      // Add events to each data point
      element.addEventListener('mouseenter', (e) => {
        if (!this.tooltipElement) return;

        const target = e.target as SVGElement;
        const datasetIndex = parseInt(target.dataset.datasetIndex || '0');
        const index = parseInt(target.dataset.index || '0');
        const x = target.dataset.x || '';
        const y = target.dataset.y || '';
        const dataset = data.datasets[datasetIndex];

        // Build tooltip content
        let tooltipContent = '';

        if (options.tooltip?.format) {
          const dataPoint = dataset.data[index];
          tooltipContent = options.tooltip.format(dataPoint, dataset);
        } else {
          tooltipContent = `
            <div style="margin-bottom: 4px">
              <span style="font-weight: bold; color: ${dataset.color || this.getDefaultColor(datasetIndex)}">${dataset.label || `Series ${datasetIndex + 1}`}:</span>
              <span>${x}, ${y}</span>
            </div>
          `;

          // Show percentage for pie charts
          if (target.dataset.percentage) {
            tooltipContent += `<div>${target.dataset.percentage}%</div>`;
          }
        }

        this.tooltipElement.innerHTML = tooltipContent;
        this.tooltipElement.style.display = 'block';

        // Position tooltip
        const rect = container.getBoundingClientRect();
        const elementRect = target.getBoundingClientRect();
        const tooltipRect = this.tooltipElement.getBoundingClientRect();

        let tooltipX = elementRect.left - rect.left + container.scrollLeft + 10;
        let tooltipY = elementRect.top - rect.top + container.scrollTop + 10;

        // Adjust position to ensure tooltip stays within container
        if (tooltipX + tooltipRect.width > container.clientWidth) {
          tooltipX = elementRect.left - rect.left - tooltipRect.width - 10;
        }

        if (tooltipY + tooltipRect.height > container.clientHeight) {
          tooltipY = elementRect.top - rect.top - tooltipRect.height - 10;
        }

        this.tooltipElement.style.left = `${tooltipX}px`;
        this.tooltipElement.style.top = `${tooltipY}px`;
      });

      element.addEventListener('mouseleave', () => {
        if (this.tooltipElement) {
          this.tooltipElement.style.display = 'none';
        }
      });
    });
  }

  /**
   * Calculate scales for axes
   */
  private calculateScales(
    data: ChartData,
    chartArea: any,
    xAxis: ChartOptions['axes']['x'],
    yAxis: ChartOptions['axes']['y']
  ): { x: { min: number, max: number }, y: { min: number, max: number } } {
    const { datasets } = data;

    // Find min and max x values
    let xMin = xAxis?.min !== undefined ? Number(xAxis.min) : Infinity;
    let xMax = xAxis?.max !== undefined ? Number(xAxis.max) : -Infinity;

    // Find min and max y values
    let yMin = yAxis?.min !== undefined ? yAxis.min : Infinity;
    let yMax = yAxis?.max !== undefined ? yAxis.max : -Infinity;

    datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        // Handle x values
        let xValue = point.x;
        if (typeof xValue === 'string' && xAxis?.type === 'category') {
          // For category axes, use index
          xValue = 0; // Will be handled later
        } else if (xValue instanceof Date && xAxis?.type === 'time') {
          xValue = xValue.getTime();
        } else if (typeof xValue === 'string') {
          xValue = parseFloat(xValue);
        }

        if (typeof xValue === 'number' && !isNaN(xValue)) {
          xMin = Math.min(xMin, xValue);
          xMax = Math.max(xMax, xValue);
        }

        // Handle y values
        if (typeof point.y === 'number') {
          yMin = Math.min(yMin, point.y);
          yMax = Math.max(yMax, point.y);
        }
      });
    });

    // Handle case where all values are the same
    if (xMin === xMax) {
      xMin -= 1;
      xMax += 1;
    }

    if (yMin === yMax) {
      yMin = yMin === 0 ? -1 : yMin * 0.9;
      yMax = yMax === 0 ? 1 : yMax * 1.1;
    }

    // For Y axis, typically we want to include zero if it's nearby
    if (yAxis?.type === 'linear' && yMin > 0 && yMin < yMax * 0.3) {
      yMin = 0;
    }

    if (yAxis?.type === 'linear' && yMax < 0 && yMax > yMin * 0.3) {
      yMax = 0;
    }

    // Add some padding
    const xRange = xMax - xMin;
    const yRange = yMax - yMin;

    xMin -= xRange * 0.05;
    xMax += xRange * 0.05;
    yMin -= yRange * 0.05;
    yMax += yRange * 0.05;

    return {
      x: { min: xMin, max: xMax },
      y: { min: yMin, max: yMax }
    };
  }

  /**
   * Map a data value to a pixel position
   */
  private mapValueToPixel(
    value: any,
    scale: { min: number; max: number },
    pixelMin: number,
    pixelMax: number,
    invert = false
  ): number {
    let normalizedValue: number;

    if (value instanceof Date) {
      normalizedValue = value.getTime();
    } else if (typeof value === 'string') {
      normalizedValue = parseFloat(value);
      if (isNaN(normalizedValue)) {
        normalizedValue = 0; // Fallback for categories
      }
    } else {
      normalizedValue = Number(value);
    }

    const normalizedPosition = (normalizedValue - scale.min) / (scale.max - scale.min);
    return invert
      ? pixelMax - normalizedPosition * (pixelMax - pixelMin)
      : pixelMin + normalizedPosition * (pixelMax - pixelMin);
  }

  /**
   * Get all unique x values from all datasets
   */
  private getAllXValues(data: ChartData): string[] {
    const uniqueValues = new Set<string>();

    data.datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        uniqueValues.add(String(point.x));
      });
    });

    return Array.from(uniqueValues);
  }

  /**
   * Get a default color based on index
   */
  private getDefaultColor(index: number): string {
    const colors = [
      '#4e79a7',
      '#f28e2c',
      '#e15759',
      '#76b7b2',
      '#59a14f',
      '#edc949',
      '#af7aa1',
      '#ff9da7',
      '#9c755f',
      '#bab0ab'
    ];

    return colors[index % colors.length];
  }

  /**
   * Convert hex color to rgba for transparency
   */
  private hexToRgba(hex: string, alpha: number): string {
    // Handle shorthand hex
    const shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i;
    hex = hex.replace(shorthandRegex, (m, r, g, b) => r + r + g + g + b + b);

    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);

    if (!result) {
      return `rgba(0, 0, 0, ${alpha})`;
    }

    return `rgba(${parseInt(result[1], 16)}, ${parseInt(result[2], 16)}, ${parseInt(result[3], 16)}, ${alpha})`;
  }

  /**
   * Get color for heatmap based on normalized value
   */
  private getHeatmapColor(normalizedValue: number): string {
    // Use a multi-stop gradient: blue -> cyan -> green -> yellow -> red
    const stops = [
      { value: 0.0, color: [0, 0, 255] },      // blue
      { value: 0.25, color: [0, 255, 255] },   // cyan
      { value: 0.5, color: [0, 255, 0] },      // green
      { value: 0.75, color: [255, 255, 0] },   // yellow
      { value: 1.0, color: [255, 0, 0] }       // red
    ];

    // Find the two stops that our value is between
    let i = 0;
    while (i < stops.length - 1 && normalizedValue > stops[i + 1].value) {
      i++;
    }

    if (i === stops.length - 1) {
      const [r, g, b] = stops[i].color;
      return `rgb(${r}, ${g}, ${b})`;
    }

    // Interpolate between the two stops
    const lowerStop = stops[i];
    const upperStop = stops[i + 1];
    const range = upperStop.value - lowerStop.value;
    const relativePosition = (normalizedValue - lowerStop.value) / range;

    const r = Math.round(lowerStop.color[0] + relativePosition * (upperStop.color[0] - lowerStop.color[0]));
    const g = Math.round(lowerStop.color[1] + relativePosition * (upperStop.color[1] - lowerStop.color[1]));
    const b = Math.round(lowerStop.color[2] + relativePosition * (upperStop.color[2] - lowerStop.color[2]));

    return `rgb(${r}, ${g}, ${b})`;
  }
}

import { ChartData, ChartOptions, ChartRenderer, ChartType } from '../Chart';

/**
 * WebGL-based chart renderer implementation.
 * Uses WebGL for high-performance rendering of charts with very large datasets.
 */
export class WebGLRenderer implements ChartRenderer {
  private canvas: HTMLCanvasElement | null = null;
  private gl: WebGLRenderingContext | null = null;
  private containerWidth = 0;
  private containerHeight = 0;
  private resolutionScale = window.devicePixelRatio || 1;
  private isInitialized = false;
  private lastRenderTime = 0;
  private tooltipElement: HTMLDivElement | null = null;
  private shaders: Map<string, { program: WebGLProgram; attributes: Map<string, number>; uniforms: Map<string, WebGLUniformLocation> }> = new Map();
  private svgCanvas: SVGSVGElement | null = null; // SVG layer for text and axes

  private theme = {
    light: {
      textColor: '#333333',
      gridColor: '#e0e0e0',
      axisColor: '#666666',
      backgroundColor: 'transparent'
    },
    dark: {
      textColor: '#e0e0e0',
      gridColor: '#444444',
      axisColor: '#999999',
      backgroundColor: 'transparent'
    }
  };

  private buffers: {
    points?: {
      position: WebGLBuffer | null;
      color: WebGLBuffer | null;
      indices: WebGLBuffer | null;
      count: number;
    };
    lines?: {
      position: WebGLBuffer | null;
      color: WebGLBuffer | null;
      indices: WebGLBuffer | null;
      count: number;
    };
  } = {};

  /**
   * Render a chart onto the container element
   */
  public render(
    container: HTMLElement,
    data: ChartData,
    options: ChartOptions,
    type: ChartType
  ): void {
    this.initialize(container, options);
    this.update(container, data, options, type);
  }

  /**
   * Updates the chart with new data or options
   */
  public update(
    container: HTMLElement,
    data: ChartData,
    options: ChartOptions,
    type: ChartType
  ): void {
    if (!this.isInitialized) {
      this.initialize(container, options);
    }

    const startTime = performance.now();

    // Update dimensions if needed
    this.updateDimensions(container);

    // Clear canvas
    if (this.gl) {
      this.gl.clearColor(0, 0, 0, 0);
      this.gl.clear(this.gl.COLOR_BUFFER_BIT);
    }

    // Clear SVG layer
    if (this.svgCanvas) {
      while (this.svgCanvas.firstChild) {
        this.svgCanvas.removeChild(this.svgCanvas.firstChild);
      }
    }

    // Calculate chart area with padding
    const padding = options.padding || {};
    const chartArea = {
      left: padding.left || 0,
      right: this.containerWidth - (padding.right || 0),
      top: padding.top || 0,
      bottom: this.containerHeight - (padding.bottom || 0)
    };

    // Render the chart based on type
    try {
      switch (type) {
        case 'line':
          this.renderLineChart(data, options, chartArea);
          break;
        case 'scatter':
          this.renderScatterChart(data, options, chartArea);
          break;
        case 'area':
          this.renderAreaChart(data, options, chartArea);
          break;
        case 'bar':
        case 'pie':
        case 'radar':
        case 'heatmap':
          // For chart types not optimized for WebGL, fall back to SVG rendering
          this.renderFallbackChart(data, options, type, chartArea);
          break;
        default:
          throw new Error(`Unsupported chart type: ${type}`);
      }

      // Render axes and other SVG elements
      if (type !== 'pie' && type !== 'radar') {
        this.renderAxes(data, options, chartArea);
      }

      // Render legend if enabled
      if (options.legend?.visible) {
        this.renderLegend(data, options, chartArea);
      }

      // Setup tooltips if enabled
      if (options.tooltip?.enabled) {
        this.setupTooltip(container, data, options, chartArea);
      } else if (this.tooltipElement) {
        this.tooltipElement.remove();
        this.tooltipElement = null;
      }
    } catch (error) {
      console.error('WebGL rendering error:', error);
      // Fall back to SVG rendering if WebGL fails
      this.renderFallbackChart(data, options, type, chartArea);
    }

    this.lastRenderTime = performance.now() - startTime;
  }

  /**
   * Destroys the renderer, cleaning up any resources
   */
  public destroy(): void {
    if (this.canvas && this.canvas.parentElement) {
      this.canvas.parentElement.removeChild(this.canvas);
    }

    if (this.svgCanvas && this.svgCanvas.parentElement) {
      this.svgCanvas.parentElement.removeChild(this.svgCanvas);
    }

    if (this.tooltipElement) {
      this.tooltipElement.remove();
    }

    // Delete WebGL resources
    if (this.gl) {
      // Delete shaders
      for (const shader of this.shaders.values()) {
        this.gl.deleteProgram(shader.program);
      }

      // Delete buffers
      if (this.buffers.points) {
        this.gl.deleteBuffer(this.buffers.points.position);
        this.gl.deleteBuffer(this.buffers.points.color);
        this.gl.deleteBuffer(this.buffers.points.indices);
      }

      if (this.buffers.lines) {
        this.gl.deleteBuffer(this.buffers.lines.position);
        this.gl.deleteBuffer(this.buffers.lines.color);
        this.gl.deleteBuffer(this.buffers.lines.indices);
      }
    }

    this.canvas = null;
    this.gl = null;
    this.svgCanvas = null;
    this.tooltipElement = null;
    this.isInitialized = false;
    this.shaders.clear();
    this.buffers = {};
  }

  /**
   * Returns the current status of the renderer
   */
  public getStatus(): { isInitialized: boolean; lastRenderTime?: number } {
    return {
      isInitialized: this.isInitialized,
      lastRenderTime: this.lastRenderTime
    };
  }

  /**
   * Initialize the WebGL renderer
   */
  private initialize(container: HTMLElement, options: ChartOptions): void {
    // Clean existing elements if any
    const existingCanvas = container.querySelector('canvas');
    if (existingCanvas) {
      container.removeChild(existingCanvas);
    }

    const existingSvg = container.querySelector('svg');
    if (existingSvg) {
      container.removeChild(existingSvg);
    }

    // Create canvas
    this.canvas = document.createElement('canvas');
    this.canvas.style.position = 'absolute';
    this.canvas.style.top = '0';
    this.canvas.style.left = '0';
    this.canvas.style.width = '100%';
    this.canvas.style.height = '100%';
    container.appendChild(this.canvas);

    // Get WebGL context
    this.gl = this.canvas.getContext('webgl', {
      antialias: true,
      alpha: true,
      premultipliedAlpha: false
    });

    if (!this.gl) {
      throw new Error('WebGL is not supported by your browser');
    }

    // Create SVG layer for text and axes (on top of WebGL)
    this.svgCanvas = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
    this.svgCanvas.setAttribute('xmlns', 'http://www.w3.org/2000/svg');
    this.svgCanvas.style.position = 'absolute';
    this.svgCanvas.style.top = '0';
    this.svgCanvas.style.left = '0';
    this.svgCanvas.style.width = '100%';
    this.svgCanvas.style.height = '100%';
    this.svgCanvas.style.pointerEvents = 'none'; // Allow clicks to pass through
    container.appendChild(this.svgCanvas);

    // Make sure container has position relative
    if (getComputedStyle(container).position === 'static') {
      container.style.position = 'relative';
    }

    // Set dimensions
    this.updateDimensions(container);

    // Initialize shaders
    this.initShaders();

    this.isInitialized = true;
  }

  /**
   * Update canvas dimensions based on container size
   */
  private updateDimensions(container: HTMLElement): void {
    if (!this.canvas || !this.gl || !this.svgCanvas) return;

    const rect = container.getBoundingClientRect();
    this.containerWidth = rect.width;
    this.containerHeight = rect.height;

    // Set canvas dimensions with higher resolution for retina displays
    this.canvas.width = this.containerWidth * this.resolutionScale;
    this.canvas.height = this.containerHeight * this.resolutionScale;

    // Set SVG dimensions
    this.svgCanvas.setAttribute('width', this.containerWidth.toString());
    this.svgCanvas.setAttribute('height', this.containerHeight.toString());
    this.svgCanvas.setAttribute('viewBox', `0 0 ${this.containerWidth} ${this.containerHeight}`);

    // Update WebGL viewport
    this.gl.viewport(0, 0, this.canvas.width, this.canvas.height);
  }

  /**
   * Initialize WebGL shaders
   */
  private initShaders(): void {
    if (!this.gl) return;

    // Point shader program
    const pointVsSource = `
      attribute vec2 a_position;
      attribute vec4 a_color;

      uniform vec2 u_resolution;
      uniform float u_pointSize;

      varying vec4 v_color;

      void main() {
        // Convert position from pixels to clip space
        vec2 clipSpace = (a_position / u_resolution) * 2.0 - 1.0;

        // Flip y axis (WebGL has y=1 at the top)
        gl_Position = vec4(clipSpace.x, -clipSpace.y, 0, 1);
        gl_PointSize = u_pointSize;

        v_color = a_color;
      }
    `;

    const pointFsSource = `
      precision mediump float;

      varying vec4 v_color;

      void main() {
        // Create circular points
        vec2 center = gl_PointCoord - vec2(0.5, 0.5);
        float dist = length(center);

        if (dist > 0.5) {
          discard;
        }

        gl_FragColor = v_color;
      }
    `;

    // Line shader program
    const lineVsSource = `
      attribute vec2 a_position;
      attribute vec4 a_color;

      uniform vec2 u_resolution;

      varying vec4 v_color;

      void main() {
        // Convert position from pixels to clip space
        vec2 clipSpace = (a_position / u_resolution) * 2.0 - 1.0;

        // Flip y axis (WebGL has y=1 at the top)
        gl_Position = vec4(clipSpace.x, -clipSpace.y, 0, 1);

        v_color = a_color;
      }
    `;

    const lineFsSource = `
      precision mediump float;

      varying vec4 v_color;

      void main() {
        gl_FragColor = v_color;
      }
    `;

    // Area shader program
    const areaVsSource = `
      attribute vec2 a_position;
      attribute vec4 a_color;

      uniform vec2 u_resolution;

      varying vec4 v_color;
      varying vec2 v_position;

      void main() {
        // Convert position from pixels to clip space
        vec2 clipSpace = (a_position / u_resolution) * 2.0 - 1.0;

        // Flip y axis (WebGL has y=1 at the top)
        gl_Position = vec4(clipSpace.x, -clipSpace.y, 0, 1);

        v_color = a_color;
        v_position = a_position;
      }
    `;

    const areaFsSource = `
      precision mediump float;

      varying vec4 v_color;
      varying vec2 v_position;

      uniform float u_baseline;

      void main() {
        // Calculate opacity based on distance from baseline
        float alpha = 1.0 - (abs(v_position.y - u_baseline) / 300.0);
        alpha = clamp(alpha, 0.1, 0.6);

        gl_FragColor = vec4(v_color.rgb, v_color.a * alpha);
      }
    `;

    // Compile and link shaders
    const pointProgram = this.createShaderProgram(pointVsSource, pointFsSource);
    const lineProgram = this.createShaderProgram(lineVsSource, lineFsSource);
    const areaProgram = this.createShaderProgram(areaVsSource, areaFsSource);

    if (!pointProgram || !lineProgram || !areaProgram) {
      throw new Error('Failed to create shader programs');
    }

    // Point shader attributes and uniforms
    const pointAttributes = new Map<string, number>();
    const pointUniforms = new Map<string, WebGLUniformLocation>();

    pointAttributes.set('a_position', this.gl.getAttribLocation(pointProgram, 'a_position'));
    pointAttributes.set('a_color', this.gl.getAttribLocation(pointProgram, 'a_color'));

    const pointResolutionUniform = this.gl.getUniformLocation(pointProgram, 'u_resolution');
    const pointSizeUniform = this.gl.getUniformLocation(pointProgram, 'u_pointSize');

    if (pointResolutionUniform && pointSizeUniform) {
      pointUniforms.set('u_resolution', pointResolutionUniform);
      pointUniforms.set('u_pointSize', pointSizeUniform);
    }

    // Line shader attributes and uniforms
    const lineAttributes = new Map<string, number>();
    const lineUniforms = new Map<string, WebGLUniformLocation>();

    lineAttributes.set('a_position', this.gl.getAttribLocation(lineProgram, 'a_position'));
    lineAttributes.set('a_color', this.gl.getAttribLocation(lineProgram, 'a_color'));

    const lineResolutionUniform = this.gl.getUniformLocation(lineProgram, 'u_resolution');

    if (lineResolutionUniform) {
      lineUniforms.set('u_resolution', lineResolutionUniform);
    }

    // Area shader attributes and uniforms
    const areaAttributes = new Map<string, number>();
    const areaUniforms = new Map<string, WebGLUniformLocation>();

    areaAttributes.set('a_position', this.gl.getAttribLocation(areaProgram, 'a_position'));
    areaAttributes.set('a_color', this.gl.getAttribLocation(areaProgram, 'a_color'));

    const areaResolutionUniform = this.gl.getUniformLocation(areaProgram, 'u_resolution');
    const areaBaselineUniform = this.gl.getUniformLocation(areaProgram, 'u_baseline');

    if (areaResolutionUniform && areaBaselineUniform) {
      areaUniforms.set('u_resolution', areaResolutionUniform);
      areaUniforms.set('u_baseline', areaBaselineUniform);
    }

    // Store shader programs
    this.shaders.set('point', { program: pointProgram, attributes: pointAttributes, uniforms: pointUniforms });
    this.shaders.set('line', { program: lineProgram, attributes: lineAttributes, uniforms: lineUniforms });
    this.shaders.set('area', { program: areaProgram, attributes: areaAttributes, uniforms: areaUniforms });
  }

  /**
   * Create a WebGL shader program
   */
  private createShaderProgram(vsSource: string, fsSource: string): WebGLProgram | null {
    if (!this.gl) return null;

    const vertexShader = this.compileShader(this.gl.VERTEX_SHADER, vsSource);
    const fragmentShader = this.compileShader(this.gl.FRAGMENT_SHADER, fsSource);

    if (!vertexShader || !fragmentShader) {
      return null;
    }

    const program = this.gl.createProgram();
    if (!program) {
      return null;
    }

    this.gl.attachShader(program, vertexShader);
    this.gl.attachShader(program, fragmentShader);
    this.gl.linkProgram(program);

    if (!this.gl.getProgramParameter(program, this.gl.LINK_STATUS)) {
      console.error('Unable to link shader program:', this.gl.getProgramInfoLog(program));
      this.gl.deleteProgram(program);
      return null;
    }

    return program;
  }

  /**
   * Compile a WebGL shader
   */
  private compileShader(type: number, source: string): WebGLShader | null {
    if (!this.gl) return null;

    const shader = this.gl.createShader(type);
    if (!shader) {
      return null;
    }

    this.gl.shaderSource(shader, source);
    this.gl.compileShader(shader);

    if (!this.gl.getShaderParameter(shader, this.gl.COMPILE_STATUS)) {
      console.error('Error compiling shader:', this.gl.getShaderInfoLog(shader));
      this.gl.deleteShader(shader);
      return null;
    }

    return shader;
  }

  /**
   * Render line chart using WebGL
   */
  private renderLineChart(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.gl) return;

    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };

    // Calculate scales
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);

    // Prepare WebGL buffers
    const lineVertices: number[] = [];
    const lineColors: number[] = [];
    const lineIndices: number[] = [];

    const pointVertices: number[] = [];
    const pointColors: number[] = [];
    const pointIndices: number[] = [];

    let lineIndexOffset = 0;
    let pointIndexOffset = 0;

    // Process each dataset
    datasets.forEach((dataset, datasetIndex) => {
      if (dataset.data.length === 0) return;

      // Parse color
      const colorStr = dataset.color || this.getDefaultColor(datasetIndex);
      const color = this.parseColor(colorStr);

      // Map data points to screen coordinates
      const points: { x: number; y: number }[] = [];
      dataset.data.forEach(point => {
        const x = this.mapValueToPixel(point.x, scales.x, chartArea.left, chartArea.right);
        const y = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);
        points.push({ x, y });
      });

      // Add line vertices
      for (let i = 0; i < points.length; i++) {
        const { x, y } = points[i];

        // Add to line vertices
        lineVertices.push(x * this.resolutionScale, y * this.resolutionScale);
        lineColors.push(color.r, color.g, color.b, color.a);

        // Add to point vertices
        pointVertices.push(x * this.resolutionScale, y * this.resolutionScale);
        pointColors.push(color.r, color.g, color.b, color.a);

        // Add point indices
        pointIndices.push(pointIndexOffset + i);

        // Add line indices (connect points)
        if (i < points.length - 1) {
          lineIndices.push(lineIndexOffset + i, lineIndexOffset + i + 1);
        }
      }

      lineIndexOffset += points.length;
      pointIndexOffset += points.length;
    });

    // Create and bind buffers
    if (lineVertices.length > 0) {
      // Line buffers
      const linePositionBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ARRAY_BUFFER, linePositionBuffer);
      this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(lineVertices), this.gl.STATIC_DRAW);

      const lineColorBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ARRAY_BUFFER, lineColorBuffer);
      this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(lineColors), this.gl.STATIC_DRAW);

      const lineIndexBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ELEMENT_ARRAY_BUFFER, lineIndexBuffer);
      this.gl.bufferData(this.gl.ELEMENT_ARRAY_BUFFER, new Uint16Array(lineIndices), this.gl.STATIC_DRAW);

      this.buffers.lines = {
        position: linePositionBuffer,
        color: lineColorBuffer,
        indices: lineIndexBuffer,
        count: lineIndices.length
      };

      // Point buffers
      const pointPositionBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ARRAY_BUFFER, pointPositionBuffer);
      this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(pointVertices), this.gl.STATIC_DRAW);

      const pointColorBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ARRAY_BUFFER, pointColorBuffer);
      this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(pointColors), this.gl.STATIC_DRAW);

      const pointIndexBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ELEMENT_ARRAY_BUFFER, pointIndexBuffer);
      this.gl.bufferData(this.gl.ELEMENT_ARRAY_BUFFER, new Uint16Array(pointIndices), this.gl.STATIC_DRAW);

      this.buffers.points = {
        position: pointPositionBuffer,
        color: pointColorBuffer,
        indices: pointIndexBuffer,
        count: pointIndices.length
      };

      // Draw lines
      this.drawLines();

      // Draw points
      this.drawPoints();
    }
  }

  /**
   * Render scatter chart using WebGL
   */
  private renderScatterChart(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.gl) return;

    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };

    // Calculate scales
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);

    // Prepare WebGL buffers
    const pointVertices: number[] = [];
    const pointColors: number[] = [];
    const pointIndices: number[] = [];

    let pointIndexOffset = 0;

    // Process each dataset
    datasets.forEach((dataset, datasetIndex) => {
      if (dataset.data.length === 0) return;

      // Parse color
      const colorStr = dataset.color || this.getDefaultColor(datasetIndex);
      const color = this.parseColor(colorStr);

      // Map data points to screen coordinates
      dataset.data.forEach((point, i) => {
        const x = this.mapValueToPixel(point.x, scales.x, chartArea.left, chartArea.right);
        const y = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);

        // Add to point vertices
        pointVertices.push(x * this.resolutionScale, y * this.resolutionScale);
        pointColors.push(color.r, color.g, color.b, color.a);

        // Add point indices
        pointIndices.push(pointIndexOffset + i);
      });

      pointIndexOffset += dataset.data.length;
    });

    // Create and bind buffers
    if (pointVertices.length > 0) {
      // Point buffers
      const pointPositionBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ARRAY_BUFFER, pointPositionBuffer);
      this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(pointVertices), this.gl.STATIC_DRAW);

      const pointColorBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ARRAY_BUFFER, pointColorBuffer);
      this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(pointColors), this.gl.STATIC_DRAW);

      const pointIndexBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ELEMENT_ARRAY_BUFFER, pointIndexBuffer);
      this.gl.bufferData(this.gl.ELEMENT_ARRAY_BUFFER, new Uint16Array(pointIndices), this.gl.STATIC_DRAW);

      this.buffers.points = {
        position: pointPositionBuffer,
        color: pointColorBuffer,
        indices: pointIndexBuffer,
        count: pointIndices.length
      };

      // Draw points with larger size
      this.drawPoints(5.0);
    }
  }

  /**
   * Render area chart using WebGL
   */
  private renderAreaChart(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.gl) return;

    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };

    // Calculate scales
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);
    const baseline = chartArea.bottom * this.resolutionScale; // For gradient calculation

    // Prepare WebGL buffers
    const areaVertices: number[] = [];
    const areaColors: number[] = [];
    const areaIndices: number[] = [];

    const lineVertices: number[] = [];
    const lineColors: number[] = [];
    const lineIndices: number[] = [];

    const pointVertices: number[] = [];
    const pointColors: number[] = [];
    const pointIndices: number[] = [];

    let areaIndexOffset = 0;
    let lineIndexOffset = 0;
    let pointIndexOffset = 0;

    // Process each dataset
    datasets.forEach((dataset, datasetIndex) => {
      if (dataset.data.length < 2) return;

      // Parse color
      const colorStr = dataset.color || this.getDefaultColor(datasetIndex);
      const color = this.parseColor(colorStr);

      // Map data points to screen coordinates
      const points: { x: number; y: number }[] = [];
      dataset.data.forEach(point => {
        const x = this.mapValueToPixel(point.x, scales.x, chartArea.left, chartArea.right);
        const y = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);
        points.push({ x, y });
      });

      // Create area triangles (using triangle strip)
      for (let i = 0; i < points.length; i++) {
        const { x, y } = points[i];

        // Add top vertices (data points)
        areaVertices.push(x * this.resolutionScale, y * this.resolutionScale);
        areaColors.push(color.r, color.g, color.b, 0.6); // Semi-transparent for gradient

        // Add bottom vertices (baseline)
        areaVertices.push(x * this.resolutionScale, chartArea.bottom * this.resolutionScale);
        areaColors.push(color.r, color.g, color.b, 0.1); // More transparent at bottom

        // Add indices for triangle strip
        if (i < points.length - 1) {
          // Create two triangles for each segment
          const topLeft = areaIndexOffset + i * 2;
          const bottomLeft = topLeft + 1;
          const topRight = topLeft + 2;
          const bottomRight = topRight + 1;

          // First triangle
          areaIndices.push(topLeft, bottomLeft, topRight);

          // Second triangle
          areaIndices.push(topRight, bottomLeft, bottomRight);
        }

        // Add to line and point buffers for the top line and points
        lineVertices.push(x * this.resolutionScale, y * this.resolutionScale);
        lineColors.push(color.r, color.g, color.b, 1.0); // Solid color for line

        pointVertices.push(x * this.resolutionScale, y * this.resolutionScale);
        pointColors.push(color.r, color.g, color.b, 1.0); // Solid color for points

        // Add point indices
        pointIndices.push(pointIndexOffset + i);

        // Add line indices (connect points)
        if (i < points.length - 1) {
          lineIndices.push(lineIndexOffset + i, lineIndexOffset + i + 1);
        }
      }

      areaIndexOffset += points.length * 2;
      lineIndexOffset += points.length;
      pointIndexOffset += points.length;
    });

    // Create and bind buffers
    if (areaVertices.length > 0) {
      // Area buffers
      const areaPositionBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ARRAY_BUFFER, areaPositionBuffer);
      this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(areaVertices), this.gl.STATIC_DRAW);

      const areaColorBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ARRAY_BUFFER, areaColorBuffer);
      this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(areaColors), this.gl.STATIC_DRAW);

      const areaIndexBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ELEMENT_ARRAY_BUFFER, areaIndexBuffer);
      this.gl.bufferData(this.gl.ELEMENT_ARRAY_BUFFER, new Uint16Array(areaIndices), this.gl.STATIC_DRAW);

      // Line buffers
      const linePositionBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ARRAY_BUFFER, linePositionBuffer);
      this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(lineVertices), this.gl.STATIC_DRAW);

      const lineColorBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ARRAY_BUFFER, lineColorBuffer);
      this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(lineColors), this.gl.STATIC_DRAW);

      const lineIndexBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ELEMENT_ARRAY_BUFFER, lineIndexBuffer);
      this.gl.bufferData(this.gl.ELEMENT_ARRAY_BUFFER, new Uint16Array(lineIndices), this.gl.STATIC_DRAW);

      // Point buffers
      const pointPositionBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ARRAY_BUFFER, pointPositionBuffer);
      this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(pointVertices), this.gl.STATIC_DRAW);

      const pointColorBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ARRAY_BUFFER, pointColorBuffer);
      this.gl.bufferData(this.gl.ARRAY_BUFFER, new Float32Array(pointColors), this.gl.STATIC_DRAW);

      const pointIndexBuffer = this.gl.createBuffer();
      this.gl.bindBuffer(this.gl.ELEMENT_ARRAY_BUFFER, pointIndexBuffer);
      this.gl.bufferData(this.gl.ELEMENT_ARRAY_BUFFER, new Uint16Array(pointIndices), this.gl.STATIC_DRAW);

      // Store buffers
      this.buffers = {
        lines: {
          position: linePositionBuffer,
          color: lineColorBuffer,
          indices: lineIndexBuffer,
          count: lineIndices.length
        },
        points: {
          position: pointPositionBuffer,
          color: pointColorBuffer,
          indices: pointIndexBuffer,
          count: pointIndices.length
        }
      };

      // Enable blending for transparent areas
      this.gl.enable(this.gl.BLEND);
      this.gl.blendFunc(this.gl.SRC_ALPHA, this.gl.ONE_MINUS_SRC_ALPHA);

      // Draw fill areas using area shader
      const areaShader = this.shaders.get('area');
      if (areaShader) {
        this.gl.useProgram(areaShader.program);

        // Set uniforms
        if (areaShader.uniforms.has('u_resolution')) {
          this.gl.uniform2f(
            areaShader.uniforms.get('u_resolution')!,
            this.canvas!.width,
            this.canvas!.height
          );
        }

        if (areaShader.uniforms.has('u_baseline')) {
          this.gl.uniform1f(
            areaShader.uniforms.get('u_baseline')!,
            baseline
          );
        }

        // Set vertex attributes
        this.gl.bindBuffer(this.gl.ARRAY_BUFFER, areaPositionBuffer);
        this.gl.vertexAttribPointer(
          areaShader.attributes.get('a_position')!,
          2, // size (x, y)
          this.gl.FLOAT, // type
          false, // normalize
          0, // stride
          0 // offset
        );
        this.gl.enableVertexAttribArray(areaShader.attributes.get('a_position')!);

        this.gl.bindBuffer(this.gl.ARRAY_BUFFER, areaColorBuffer);
        this.gl.vertexAttribPointer(
          areaShader.attributes.get('a_color')!,
          4, // size (r, g, b, a)
          this.gl.FLOAT, // type
          false, // normalize
          0, // stride
          0 // offset
        );
        this.gl.enableVertexAttribArray(areaShader.attributes.get('a_color')!);

        // Draw triangles
        this.gl.bindBuffer(this.gl.ELEMENT_ARRAY_BUFFER, areaIndexBuffer);
        this.gl.drawElements(
          this.gl.TRIANGLES,
          areaIndices.length,
          this.gl.UNSIGNED_SHORT,
          0
        );
      }

      // Draw lines
      this.drawLines();

      // Draw points
      this.drawPoints();
    }
  }

  /**
   * Draw lines using the line shader
   */
  private drawLines(): void {
    if (!this.gl || !this.buffers.lines) return;

    const lineShader = this.shaders.get('line');
    if (!lineShader) return;

    this.gl.useProgram(lineShader.program);

    // Set uniforms
    if (lineShader.uniforms.has('u_resolution')) {
      this.gl.uniform2f(
        lineShader.uniforms.get('u_resolution')!,
        this.canvas!.width,
        this.canvas!.height
      );
    }

    // Set vertex attributes
    this.gl.bindBuffer(this.gl.ARRAY_BUFFER, this.buffers.lines.position!);
    this.gl.vertexAttribPointer(
      lineShader.attributes.get('a_position')!,
      2, // size (x, y)
      this.gl.FLOAT, // type
      false, // normalize
      0, // stride
      0 // offset
    );
    this.gl.enableVertexAttribArray(lineShader.attributes.get('a_position')!);

    this.gl.bindBuffer(this.gl.ARRAY_BUFFER, this.buffers.lines.color!);
    this.gl.vertexAttribPointer(
      lineShader.attributes.get('a_color')!,
      4, // size (r, g, b, a)
      this.gl.FLOAT, // type
      false, // normalize
      0, // stride
      0 // offset
    );
    this.gl.enableVertexAttribArray(lineShader.attributes.get('a_color')!);

    // Draw lines
    this.gl.bindBuffer(this.gl.ELEMENT_ARRAY_BUFFER, this.buffers.lines.indices!);
    this.gl.drawElements(
      this.gl.LINES,
      this.buffers.lines.count,
      this.gl.UNSIGNED_SHORT,
      0
    );
  }

  /**
   * Draw points using the point shader
   */
  private drawPoints(pointSize = 3.0): void {
    if (!this.gl || !this.buffers.points) return;

    const pointShader = this.shaders.get('point');
    if (!pointShader) return;

    this.gl.useProgram(pointShader.program);

    // Set uniforms
    if (pointShader.uniforms.has('u_resolution')) {
      this.gl.uniform2f(
        pointShader.uniforms.get('u_resolution')!,
        this.canvas!.width,
        this.canvas!.height
      );
    }

    if (pointShader.uniforms.has('u_pointSize')) {
      this.gl.uniform1f(
        pointShader.uniforms.get('u_pointSize')!,
        pointSize * this.resolutionScale
      );
    }

    // Set vertex attributes
    this.gl.bindBuffer(this.gl.ARRAY_BUFFER, this.buffers.points.position!);
    this.gl.vertexAttribPointer(
      pointShader.attributes.get('a_position')!,
      2, // size (x, y)
      this.gl.FLOAT, // type
      false, // normalize
      0, // stride
      0 // offset
    );
    this.gl.enableVertexAttribArray(pointShader.attributes.get('a_position')!);

    this.gl.bindBuffer(this.gl.ARRAY_BUFFER, this.buffers.points.color!);
    this.gl.vertexAttribPointer(
      pointShader.attributes.get('a_color')!,
      4, // size (r, g, b, a)
      this.gl.FLOAT, // type
      false, // normalize
      0, // stride
      0 // offset
    );
    this.gl.enableVertexAttribArray(pointShader.attributes.get('a_color')!);

    // Draw points
    this.gl.bindBuffer(this.gl.ELEMENT_ARRAY_BUFFER, this.buffers.points.indices!);
    this.gl.drawElements(
      this.gl.POINTS,
      this.buffers.points.count,
      this.gl.UNSIGNED_SHORT,
      0
    );
  }

  /**
   * Fallback to SVG rendering for chart types not optimized for WebGL
   */
  private renderFallbackChart(
    data: ChartData,
    options: ChartOptions,
    type: ChartType,
    chartArea: any
  ): void {
    if (!this.svgCanvas) return;

    // Create SVG renderer instance from imported SVGRenderer
    const svgRenderer = new SVGFallbackRenderer(this.svgCanvas);
    svgRenderer.render(data, options, type, chartArea);
  }

  /**
   * Render axes for charts
   */
  private renderAxes(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.svgCanvas) return;

    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);
    const themeColors = options.theme === 'dark' ? this.theme.dark : this.theme.light;

    // Create axes group
    const axesGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    axesGroup.setAttribute('class', 'axes');
    this.svgCanvas.appendChild(axesGroup);

    // Draw x-axis
    const xAxisLine = document.createElementNS('http://www.w3.org/2000/svg', 'line');
    xAxisLine.setAttribute('x1', chartArea.left.toString());
    xAxisLine.setAttribute('y1', chartArea.bottom.toString());
    xAxisLine.setAttribute('x2', chartArea.right.toString());
    xAxisLine.setAttribute('y2', chartArea.bottom.toString());
    xAxisLine.setAttribute('stroke', themeColors.axisColor);
    xAxisLine.setAttribute('stroke-width', '1');
    axesGroup.appendChild(xAxisLine);

    // Draw y-axis
    const yAxisLine = document.createElementNS('http://www.w3.org/2000/svg', 'line');
    yAxisLine.setAttribute('x1', chartArea.left.toString());
    yAxisLine.setAttribute('y1', chartArea.top.toString());
    yAxisLine.setAttribute('x2', chartArea.left.toString());
    yAxisLine.setAttribute('y2', chartArea.bottom.toString());
    yAxisLine.setAttribute('stroke', themeColors.axisColor);
    yAxisLine.setAttribute('stroke-width', '1');
    axesGroup.appendChild(yAxisLine);

    // Draw grid lines if enabled
    if (xAxis.grid || yAxis.grid) {
      this.drawGrid(scales, chartArea, options, axesGroup);
    }

    // Draw x-axis ticks and labels
    const xTickCount = xAxis.tickCount || 5;
    const xStep = (scales.x.max - scales.x.min) / (xTickCount - 1);

    for (let i = 0; i < xTickCount; i++) {
      const value = scales.x.min + i * xStep;
      const x = this.mapValueToPixel(value, scales.x, chartArea.left, chartArea.right);
      const y = chartArea.bottom;

      // Draw tick
      const tick = document.createElementNS('http://www.w3.org/2000/svg', 'line');
      tick.setAttribute('x1', x.toString());
      tick.setAttribute('y1', y.toString());
      tick.setAttribute('x2', x.toString());
      tick.setAttribute('y2', (y + 5).toString());
      tick.setAttribute('stroke', themeColors.axisColor);
      tick.setAttribute('stroke-width', '1');
      axesGroup.appendChild(tick);

      // Draw label
      let tickLabel = String(value);
      if (xAxis.tickFormat) {
        tickLabel = xAxis.tickFormat(value);
      } else if (xAxis.type === 'time' && value instanceof Date) {
        tickLabel = value.toLocaleDateString();
      }

      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', x.toString());
      text.setAttribute('y', (y + 15).toString());
      text.setAttribute('text-anchor', 'middle');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '12px');
      text.textContent = tickLabel;

      axesGroup.appendChild(text);
    }

    // Draw y-axis ticks and labels
    const yTickCount = yAxis.tickCount || 5;
    const yStep = (scales.y.max - scales.y.min) / (yTickCount - 1);

    for (let i = 0; i < yTickCount; i++) {
      const value = scales.y.min + i * yStep;
      const x = chartArea.left;
      const y = this.mapValueToPixel(value, scales.y, chartArea.bottom, chartArea.top, true);

      // Draw tick
      const tick = document.createElementNS('http://www.w3.org/2000/svg', 'line');
      tick.setAttribute('x1', x.toString());
      tick.setAttribute('y1', y.toString());
      tick.setAttribute('x2', (x - 5).toString());
      tick.setAttribute('y2', y.toString());
      tick.setAttribute('stroke', themeColors.axisColor);
      tick.setAttribute('stroke-width', '1');
      axesGroup.appendChild(tick);

      // Draw label
      let tickLabel = String(value);
      if (yAxis.tickFormat) {
        tickLabel = yAxis.tickFormat(value);
      }

      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', (x - 8).toString());
      text.setAttribute('y', y.toString());
      text.setAttribute('text-anchor', 'end');
      text.setAttribute('dominant-baseline', 'middle');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '12px');
      text.textContent = tickLabel;

      axesGroup.appendChild(text);
    }

    // Draw axis labels if provided
    if (xAxis.label) {
      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', ((chartArea.left + chartArea.right) / 2).toString());
      text.setAttribute('y', (this.containerHeight - 5).toString());
      text.setAttribute('text-anchor', 'middle');
      text.setAttribute('dominant-baseline', 'bottom');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '14px');
      text.setAttribute('font-weight', 'bold');
      text.textContent = xAxis.label;

      axesGroup.appendChild(text);
    }

    if (yAxis.label) {
      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('transform', `translate(10, ${(chartArea.top + chartArea.bottom) / 2}) rotate(-90)`);
      text.setAttribute('text-anchor', 'middle');
      text.setAttribute('dominant-baseline', 'bottom');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '14px');
      text.setAttribute('font-weight', 'bold');
      text.textContent = yAxis.label;

      axesGroup.appendChild(text);
    }
  }

  /**
   * Draw grid lines
   */
  private drawGrid(
    scales: any,
    chartArea: any,
    options: ChartOptions,
    axesGroup: SVGGElement
  ): void {
    const xAxis = options.axes?.x || { type: 'linear', grid: true };
    const yAxis = options.axes?.y || { type: 'linear', grid: true };
    const themeColors = options.theme === 'dark' ? this.theme.dark : this.theme.light;

    // Create a group for the grid
    const gridGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    gridGroup.setAttribute('class', 'grid');
    axesGroup.appendChild(gridGroup);

    // Draw x-axis grid lines
    if (yAxis.grid) {
      const xTickCount = xAxis.tickCount || 5;
      const xStep = (scales.x.max - scales.x.min) / (xTickCount - 1);

      for (let i = 0; i < xTickCount; i++) {
        const value = scales.x.min + i * xStep;
        const x = this.mapValueToPixel(value, scales.x, chartArea.left, chartArea.right);

        const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
        line.setAttribute('x1', x.toString());
        line.setAttribute('y1', chartArea.top.toString());
        line.setAttribute('x2', x.toString());
        line.setAttribute('y2', chartArea.bottom.toString());
        line.setAttribute('stroke', themeColors.gridColor);
        line.setAttribute('stroke-width', '0.5');

        gridGroup.appendChild(line);
      }
    }

    // Draw y-axis grid lines
    if (xAxis.grid) {
      const yTickCount = yAxis.tickCount || 5;
      const yStep = (scales.y.max - scales.y.min) / (yTickCount - 1);

      for (let i = 0; i < yTickCount; i++) {
        const value = scales.y.min + i * yStep;
        const y = this.mapValueToPixel(value, scales.y, chartArea.bottom, chartArea.top, true);

        const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
        line.setAttribute('x1', chartArea.left.toString());
        line.setAttribute('y1', y.toString());
        line.setAttribute('x2', chartArea.right.toString());
        line.setAttribute('y2', y.toString());
        line.setAttribute('stroke', themeColors.gridColor);
        line.setAttribute('stroke-width', '0.5');

        gridGroup.appendChild(line);
      }
    }
  }

  /**
   * Render legend
   */
  private renderLegend(data: ChartData, options: ChartOptions, chartArea: any): void {
    if (!this.svgCanvas) return;

    const { datasets } = data;
    const legendOptions = options.legend || { visible: true, position: 'top' };

    if (!legendOptions.visible || datasets.length === 0) return;

    const padding = 10;
    const itemHeight = 20;
    const itemWidth = 80;
    const itemsPerRow = Math.floor((chartArea.right - chartArea.left) / itemWidth);
    const rows = Math.ceil(datasets.length / itemsPerRow);

    let startX, startY, legendWidth, legendHeight;

    legendWidth = Math.min(datasets.length, itemsPerRow) * itemWidth;
    legendHeight = rows * itemHeight;

    switch (legendOptions.position) {
      case 'top':
        startX = (chartArea.left + chartArea.right - legendWidth) / 2;
        startY = chartArea.top - legendHeight - padding;
        break;
      case 'bottom':
        startX = (chartArea.left + chartArea.right - legendWidth) / 2;
        startY = chartArea.bottom + padding;
        break;
      case 'left':
        startX = chartArea.left - legendWidth - padding;
        startY = (chartArea.top + chartArea.bottom - legendHeight) / 2;
        break;
      case 'right':
        startX = chartArea.right + padding;
        startY = (chartArea.top + chartArea.bottom - legendHeight) / 2;
        break;
      default:
        startX = (chartArea.left + chartArea.right - legendWidth) / 2;
        startY = chartArea.top - legendHeight - padding;
    }

    // Ensure legend stays within bounds
    startX = Math.max(padding, startX);
    startY = Math.max(padding, startY);

    // Create legend group
    const legendGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    legendGroup.setAttribute('class', 'legend');
    this.svgCanvas.appendChild(legendGroup);

    const themeColors = options.theme === 'dark' ? this.theme.dark : this.theme.light;

    // Draw legend items
    datasets.forEach((dataset, i) => {
      const row = Math.floor(i / itemsPerRow);
      const col = i % itemsPerRow;
      const x = startX + col * itemWidth;
      const y = startY + row * itemHeight;

      // Draw color box
      const rect = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
      rect.setAttribute('x', x.toString());
      rect.setAttribute('y', (y + 4).toString());
      rect.setAttribute('width', '12');
      rect.setAttribute('height', '12');
      rect.setAttribute('fill', dataset.color || this.getDefaultColor(i));
      rect.setAttribute('stroke', themeColors.axisColor);
      rect.setAttribute('stroke-width', '1');

      legendGroup.appendChild(rect);

      // Draw text
      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', (x + 18).toString());
      text.setAttribute('y', (y + 10).toString());
      text.setAttribute('dominant-baseline', 'middle');
      text.setAttribute('fill', themeColors.textColor);
      text.setAttribute('font-size', '12px');
      text.textContent = dataset.label || `Series ${i + 1}`;

      legendGroup.appendChild(text);
    });
  }

  /**
   * Set up tooltip for chart
   */
  private setupTooltip(
    container: HTMLElement,
    data: ChartData,
    options: ChartOptions,
    chartArea: any
  ): void {
    if (!this.canvas) return;

    // Remove existing tooltip if any
    if (this.tooltipElement) {
      this.tooltipElement.remove();
    }

    // Create tooltip element
    this.tooltipElement = document.createElement('div');
    this.tooltipElement.className = 'chart-tooltip';
    this.tooltipElement.style.position = 'absolute';
    this.tooltipElement.style.display = 'none';
    this.tooltipElement.style.backgroundColor = options.theme === 'dark' ? '#333' : '#fff';
    this.tooltipElement.style.color = options.theme === 'dark' ? '#fff' : '#333';
    this.tooltipElement.style.border = `1px solid ${options.theme === 'dark' ? '#555' : '#ddd'}`;
    this.tooltipElement.style.padding = '8px';
    this.tooltipElement.style.borderRadius = '4px';
    this.tooltipElement.style.pointerEvents = 'none';
    this.tooltipElement.style.zIndex = '1000';
    this.tooltipElement.style.fontSize = '12px';
    this.tooltipElement.style.boxShadow = '0 2px 5px rgba(0,0,0,0.1)';

    container.appendChild(this.tooltipElement);

    // Add event listener to canvas for mouse movement
    this.canvas.addEventListener('mousemove', (e) => {
      const rect = this.canvas!.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;

      // Only show tooltip if within chart area
      if (
        x < chartArea.left ||
        x > chartArea.right ||
        y < chartArea.top ||
        y > chartArea.bottom
      ) {
        this.tooltipElement!.style.display = 'none';
        return;
      }

      // Find nearest data points
      const nearestPoints = this.findNearestPoints(x, y, data, options, chartArea);

      if (nearestPoints.length === 0) {
        this.tooltipElement!.style.display = 'none';
        return;
      }

      // Build tooltip content
      let tooltipContent = '';

      nearestPoints.forEach(({ dataset, point, datasetIndex }) => {
        const formattedX = point.x instanceof Date 
          ? point.x.toLocaleDateString() 
          : point.x;

        const formattedY = typeof point.y === 'number' 
          ? point.y.toLocaleString(undefined, { maximumFractionDigits: 2 }) 
          : point.y;

        if (options.tooltip?.format) {
          tooltipContent += options.tooltip.format(point, dataset);
        } else {
          tooltipContent += `
            <div style="margin-bottom: 4px">
              <span style="font-weight: bold; color: ${dataset.color || this.getDefaultColor(datasetIndex)}">${dataset.label || `Series ${datasetIndex + 1}`}:</span>
              <span>${formattedX}, ${formattedY}</span>
            </div>
          `;
        }
      });

      // Update tooltip
      this.tooltipElement!.innerHTML = tooltipContent;
      this.tooltipElement!.style.display = 'block';

      // Position tooltip
      let tooltipX = x + 10;
      let tooltipY = y + 10;

      const tooltipRect = this.tooltipElement!.getBoundingClientRect();

      // Adjust position to ensure tooltip stays within container
      if (tooltipX + tooltipRect.width > container.clientWidth) {
        tooltipX = x - tooltipRect.width - 10;
      }

      if (tooltipY + tooltipRect.height > container.clientHeight) {
        tooltipY = y - tooltipRect.height - 10;
      }

      this.tooltipElement!.style.left = `${tooltipX}px`;
      this.tooltipElement!.style.top = `${tooltipY}px`;
    });

    this.canvas.addEventListener('mouseout', () => {
      if (this.tooltipElement) {
        this.tooltipElement.style.display = 'none';
      }
    });
  }

  /**
   * Find nearest data points to mouse position
   */
  private findNearestPoints(
    mouseX: number,
    mouseY: number,
    data: ChartData,
    options: ChartOptions,
    chartArea: any
  ): Array<{ dataset: ChartData['datasets'][0]; point: ChartData['datasets'][0]['data'][0]; datasetIndex: number }> {
    const { datasets } = data;
    const xAxis = options.axes?.x || { type: 'linear' };
    const yAxis = options.axes?.y || { type: 'linear' };
    const scales = this.calculateScales(data, chartArea, xAxis, yAxis);
    const tooltipMode = options.tooltip?.mode || 'nearest';
    const intersect = options.tooltip?.intersect !== false;

    interface NearestPoint {
      dataset: ChartData['datasets'][0];
      point: ChartData['datasets'][0]['data'][0];
      datasetIndex: number;
      distance: number;
    }

    const nearestPoints: NearestPoint[] = [];

    // Find nearest points based on chart type and tooltip mode
    datasets.forEach((dataset, datasetIndex) => {
      dataset.data.forEach(point => {
        const pointX = this.mapValueToPixel(point.x, scales.x, chartArea.left, chartArea.right);
        const pointY = this.mapValueToPixel(point.y, scales.y, chartArea.bottom, chartArea.top, true);

        // Calculate distance
        const distance = Math.sqrt(Math.pow(mouseX - pointX, 2) + Math.pow(mouseY - pointY, 2));

        nearestPoints.push({
          dataset,
          point,
          datasetIndex,
          distance
        });
      });
    });

    // Sort by distance
    nearestPoints.sort((a, b) => a.distance - b.distance);

    // Filter based on mode
    let result: typeof nearestPoints = [];

    if (tooltipMode === 'nearest') {
      // Only include the nearest point
      if (nearestPoints.length > 0) {
        if (!intersect || nearestPoints[0].distance < 20) {
          result = [nearestPoints[0]];
        }
      }
    } else if (tooltipMode === 'point') {
      // Include all points within a threshold distance
      result = nearestPoints.filter(item => {
        return !intersect || item.distance < 20;
      });
    } else if (tooltipMode === 'dataset') {
      // Include the nearest point from each dataset
      const datasetMap = new Map<number, typeof nearestPoints[0]>();

      for (const item of nearestPoints) {
        if (!datasetMap.has(item.datasetIndex) || 
            item.distance < datasetMap.get(item.datasetIndex)!.distance) {
          datasetMap.set(item.datasetIndex, item);
        }
      }

      result = Array.from(datasetMap.values()).filter(item => !intersect || item.distance < 40);
    }

    // Return without the distance property
    return result.map(({ dataset, point, datasetIndex }) => ({ dataset, point, datasetIndex }));
  }

  /**
   * Calculate scales for axes
   */
  private calculateScales(
    data: ChartData,
    chartArea: any,
    xAxis: ChartOptions['axes']['x'],
    yAxis: ChartOptions['axes']['y']
  ): { x: { min: number, max: number }, y: { min: number, max: number } } {
    const { datasets } = data;

    // Find min and max x values
    let xMin = xAxis?.min !== undefined ? Number(xAxis.min) : Infinity;
    let xMax = xAxis?.max !== undefined ? Number(xAxis.max) : -Infinity;

    // Find min and max y values
    let yMin = yAxis?.min !== undefined ? yAxis.min : Infinity;
    let yMax = yAxis?.max !== undefined ? yAxis.max : -Infinity;

    datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        // Handle x values
        let xValue = point.x;
        if (typeof xValue === 'string' && xAxis?.type === 'category') {
          // For category axes, use index
          xValue = 0; // Will be handled later
        } else if (xValue instanceof Date && xAxis?.type === 'time') {
          xValue = xValue.getTime();
        } else if (typeof xValue === 'string') {
          xValue = parseFloat(xValue);
        }

        if (typeof xValue === 'number' && !isNaN(xValue)) {
          xMin = Math.min(xMin, xValue);
          xMax = Math.max(xMax, xValue);
        }

        // Handle y values
        if (typeof point.y === 'number') {
          yMin = Math.min(yMin, point.y);
          yMax = Math.max(yMax, point.y);
        }
      });
    });

    // Handle case where all values are the same
    if (xMin === xMax) {
      xMin -= 1;
      xMax += 1;
    }

    if (yMin === yMax) {
      yMin = yMin === 0 ? -1 : yMin * 0.9;
      yMax = yMax === 0 ? 1 : yMax * 1.1;
    }

    // For Y axis, typically we want to include zero if it's nearby
    if (yAxis?.type === 'linear' && yMin > 0 && yMin < yMax * 0.3) {
      yMin = 0;
    }

    if (yAxis?.type === 'linear' && yMax < 0 && yMax > yMin * 0.3) {
      yMax = 0;
    }

    // Add some padding
    const xRange = xMax - xMin;
    const yRange = yMax - yMin;

    xMin -= xRange * 0.05;
    xMax += xRange * 0.05;
    yMin -= yRange * 0.05;
    yMax += yRange * 0.05;

    return {
      x: { min: xMin, max: xMax },
      y: { min: yMin, max: yMax }
    };
  }

  /**
   * Map a data value to a pixel position
   */
  private mapValueToPixel(
    value: any,
    scale: { min: number; max: number },
    pixelMin: number,
    pixelMax: number,
    invert = false
  ): number {
    let normalizedValue: number;

    if (value instanceof Date) {
      normalizedValue = value.getTime();
    } else if (typeof value === 'string') {
      normalizedValue = parseFloat(value);
      if (isNaN(normalizedValue)) {
        normalizedValue = 0; // Fallback for categories
      }
    } else {
      normalizedValue = Number(value);
    }

    const normalizedPosition = (normalizedValue - scale.min) / (scale.max - scale.min);
    return invert
      ? pixelMax - normalizedPosition * (pixelMax - pixelMin)
      : pixelMin + normalizedPosition * (pixelMax - pixelMin);
  }

  /**
   * Get all unique x values from all datasets
   */
  private getAllXValues(data: ChartData): string[] {
    const uniqueValues = new Set<string>();

    data.datasets.forEach(dataset => {
      dataset.data.forEach(point => {
        uniqueValues.add(String(point.x));
      });
    });

    return Array.from(uniqueValues);
  }

  /**
   * Get a default color based on index
   */
  private getDefaultColor(index: number): string {
    const colors = [
      '#4e79a7',
      '#f28e2c',
      '#e15759',
      '#76b7b2',
      '#59a14f',
      '#edc949',
      '#af7aa1',
      '#ff9da7',
      '#9c755f',
      '#bab0ab'
    ];

    return colors[index % colors.length];
  }

  /**
   * Parse color string to RGBA values
   */
  private parseColor(color: string): { r: number; g: number; b: number; a: number } {
    // Handle shorthand hex
    const shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i;
    color = color.replace(shorthandRegex, (m, r, g, b) => r + r + g + g + b + b);

    // Handle hex
    const hexRegex = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i;
    const hexResult = hexRegex.exec(color);

    if (hexResult) {
      return {
        r: parseInt(hexResult[1], 16) / 255,
        g: parseInt(hexResult[2], 16) / 255,
        b: parseInt(hexResult[3], 16) / 255,
        a: 1
      };
    }

    // Handle rgba
    const rgbaRegex = /^rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*(\d+(?:\.\d+)?))?\)$/i;
    const rgbaResult = rgbaRegex.exec(color);

    if (rgbaResult) {
      return {
        r: parseInt(rgbaResult[1], 10) / 255,
        g: parseInt(rgbaResult[2], 10) / 255,
        b: parseInt(rgbaResult[3], 10) / 255,
        a: rgbaResult[4] ? parseFloat(rgbaResult[4]) : 1
      };
    }

    // Default to black if color can't be parsed
    return { r: 0, g: 0, b: 0, a: 1 };
  }

  /**
   * Convert hex color to rgba for transparency
   */
  private hexToRgba(hex: string, alpha: number): string {
    // Handle shorthand hex
    const shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i;
    hex = hex.replace(shorthandRegex, (m, r, g, b) => r + r + g + g + b + b);

    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);

    if (!result) {
      return `rgba(0, 0, 0, ${alpha})`;
    }

    return `rgba(${parseInt(result[1], 16)}, ${parseInt(result[2], 16)}, ${parseInt(result[3], 16)}, ${alpha})`;
  }
}

/**
 * Simple SVG renderer implementation for fallback rendering
 */
class SVGFallbackRenderer {
  private svg: SVGSVGElement;

  constructor(svg: SVGSVGElement) {
    this.svg = svg;
  }

  public render(
    data: ChartData,
    options: ChartOptions,
    type: ChartType,
    chartArea: any
  ): void {
    const mainGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    mainGroup.setAttribute('class', 'chart');
    this.svg.appendChild(mainGroup);

    const textElement = document.createElementNS('http://www.w3.org/2000/svg', 'text');
    textElement.setAttribute('x', (chartArea.left + chartArea.right) / 2 + '');
    textElement.setAttribute('y', (chartArea.top + chartArea.bottom) / 2 + '');
    textElement.setAttribute('text-anchor', 'middle');
    textElement.setAttribute('dominant-baseline', 'middle');
    textElement.setAttribute('fill', '#666');
    textElement.textContent = `This chart type (${type}) is rendered using SVG for better compatibility`;

    mainGroup.appendChild(textElement);
  }
}

/**
 * ResourceFlowWorker.ts
 *
 * Web Worker implementation for offloading resource flow calculations
 * This worker handles heavy computational tasks from the ResourceFlowManager
 * to prevent UI thread blocking for large resource networks.
 */

import {
  FlowConnection,
  FlowNode,
  ResourceState,
  ResourceTransfer,
  ResourceType,
} from '../types/resources/StandardizedResourceTypes';

// Message types for communication with the main thread
type WorkerMessageType =
  | 'OPTIMIZE_FLOWS'
  | 'BATCH_PROCESS'
  | 'CALCULATE_RESOURCE_BALANCE'
  | 'OPTIMIZE_FLOW_RATES'
  | 'CALCULATE_EFFICIENCY';

// Input data structure for worker tasks
interface WorkerInput {
  type: WorkerMessageType;
  data: any;
  taskId: string;
}

// Output data structure for worker results
interface WorkerOutput {
  type: WorkerMessageType;
  result: any;
  taskId: string;
  executionTimeMs: number;
}

// Self reference for the worker context
const ctx: Worker = self as any;

// Handle messages from main thread
ctx.addEventListener('message', (event: MessageEvent<WorkerInput>) => {
  const { type, data, taskId } = event.data;
  const startTime = Date.now();

  let result: any;

  try {
    switch (type) {
      case 'OPTIMIZE_FLOWS':
        result = optimizeFlows(data.nodes, data.connections, data.resourceStates);
        break;

      case 'BATCH_PROCESS':
        result = processBatch(data.nodes, data.connections, data.batchSize);
        break;

      case 'CALCULATE_RESOURCE_BALANCE':
        result = calculateResourceBalance(
          data.producers,
          data.consumers,
          data.storages,
          data.connections
        );
        break;

      case 'OPTIMIZE_FLOW_RATES':
        result = optimizeFlowRates(data.connections, data.availability, data.demand);
        break;

      case 'CALCULATE_EFFICIENCY':
        result = calculateNetworkEfficiency(data.network);
        break;

      default:
        throw new Error(`Unknown task type: ${type}`);
    }

    // Send successful result back to main thread
    const endTime = Date.now();
    ctx.postMessage({
      type,
      result,
      taskId,
      executionTimeMs: endTime - startTime,
    } as WorkerOutput);
  } catch (error) {
    // Send error back to main thread
    ctx.postMessage({
      type,
      result: null,
      error: error instanceof Error ? error.message : 'Unknown error',
      taskId,
      executionTimeMs: Date.now() - startTime,
    });
  }
});

/**
 * Main optimization function - handles the complete flow optimization process
 */
function optimizeFlows(
  nodes: FlowNode[],
  connections: FlowConnection[],
  resourceStates: Map<ResourceType, ResourceState>
) {
  // Filter active nodes and connections
  const activeNodes = nodes.filter(node => node.active);
  const activeConnections = connections.filter(conn => conn.active);

  // Categorize nodes by type
  const producers = activeNodes.filter(node => node.type === 'producer');
  const consumers = activeNodes.filter(node => node.type === 'consumer');
  const storages = activeNodes.filter(node => node.type === 'storage');

  // Calculate resource balance
  const { availability, demand } = calculateResourceBalance(
    producers,
    consumers,
    storages,
    activeConnections
  );

  // Identify resource issues
  const { bottlenecks, underutilized } = identifyResourceIssues(availability, demand);

  // Optimize flow rates
  const { updatedConnections, transfers } = optimizeFlowRates(
    activeConnections,
    availability,
    demand
  );

  // Return optimization results
  return {
    transfers,
    updatedConnections,
    bottlenecks,
    underutilized,
    performanceMetrics: {
      nodesProcessed: activeNodes.length,
      connectionsProcessed: activeConnections.length,
      transfersGenerated: transfers.length,
    },
  };
}

/**
 * Process a batch of nodes and connections
 */
function processBatch(nodes: FlowNode[], connections: FlowConnection[], batchSize: number) {
  const results = [];
  const batchCount = Math.ceil(nodes.length / batchSize);

  for (let i = 0; i < batchCount; i++) {
    const start = i * batchSize;
    const end = Math.min(start + batchSize, nodes.length);
    const nodeBatch = nodes.slice(start, end);

    // Process each node in the batch
    for (const node of nodeBatch) {
      // Simplified processing logic for demonstration
      const nodeConnections = connections.filter(
        conn => conn.source === node.id || conn.target === node.id
      );

      results.push({
        nodeId: node.id,
        connectionCount: nodeConnections.length,
        processed: true,
      });
    }
  }

  return {
    batchResults: results,
    totalProcessed: results.length,
  };
}

/**
 * Calculate resource balance between producers, consumers, and storage
 */
function calculateResourceBalance(
  producers: FlowNode[],
  consumers: FlowNode[],
  storages: FlowNode[],
  activeConnections: FlowConnection[]
): {
  availability: Partial<Record<ResourceType, number>>;
  demand: Partial<Record<ResourceType, number>>;
} {
  const availability: Partial<Record<ResourceType, number>> = {};
  const demand: Partial<Record<ResourceType, number>> = {};

  // Calculate production capacity
  for (const producer of producers) {
    for (const resourceType of producer.resources) {
      // This is a simplified version - the actual implementation would calculate
      // based on producer capacity, efficiency, etc.
      availability[resourceType] = (availability[resourceType] || 0) + 10;
    }
  }

  // Calculate consumer demand
  for (const consumer of consumers) {
    for (const resourceType of consumer.resources) {
      // Simplified demand calculation
      demand[resourceType] = (demand[resourceType] || 0) + 5;
    }
  }

  // Factor in storage capacity
  for (const storage of storages) {
    for (const resourceType of storage.resources) {
      // Simplified storage calculation
      availability[resourceType] = (availability[resourceType] || 0) + 2;
    }
  }

  return { availability, demand };
}

/**
 * Identify resource bottlenecks and underutilized resources
 */
function identifyResourceIssues(
  availability: Partial<Record<ResourceType, number>>,
  demand: Partial<Record<ResourceType, number>>
): {
  bottlenecks: string[];
  underutilized: string[];
} {
  const bottlenecks: string[] = [];
  const underutilized: string[] = [];

  // Compare availability and demand for each resource type
  for (const resourceType in demand) {
    if (Object.prototype.hasOwnProperty.call(demand, resourceType)) {
      const availableAmount = availability[resourceType as ResourceType] || 0;
      const demandAmount = demand[resourceType as ResourceType] || 0;

      // Check for bottlenecks (demand > availability)
      if (demandAmount > availableAmount) {
        bottlenecks.push(resourceType);
      }

      // Check for underutilized resources (availability > demand * 1.5)
      if (availableAmount > demandAmount * 1.5) {
        underutilized.push(resourceType);
      }
    }
  }

  return { bottlenecks, underutilized };
}

/**
 * Optimize flow rates based on resource availability and demand
 */
function optimizeFlowRates(
  activeConnections: FlowConnection[],
  availability: Partial<Record<ResourceType, number>>,
  demand: Partial<Record<ResourceType, number>>
): {
  updatedConnections: FlowConnection[];
  transfers: ResourceTransfer[];
} {
  const updatedConnections: FlowConnection[] = [];
  const transfers: ResourceTransfer[] = [];

  // Group connections by resource type
  const connectionsByResource: Record<string, FlowConnection[]> = {};

  for (const connection of activeConnections) {
    const resourceType = connection.resourceType as string;
    connectionsByResource[resourceType] = connectionsByResource[resourceType] || [];
    connectionsByResource[resourceType].push(connection);
  }

  // Process each resource type
  for (const resourceType in connectionsByResource) {
    if (Object.prototype.hasOwnProperty.call(connectionsByResource, resourceType)) {
      const connections = connectionsByResource[resourceType];
      const availableAmount = availability[resourceType as ResourceType] || 0;
      const demandAmount = demand[resourceType as ResourceType] || 0;

      // Skip if no demand or availability
      if (demandAmount === 0 || availableAmount === 0) {
        continue;
      }

      // Sort connections by priority
      connections.sort((a, b) => (b.priority?.priority || 0) - (a.priority?.priority || 0));

      // Distribute resources based on priority
      let remainingAvailability = availableAmount;

      for (const connection of connections) {
        if (remainingAvailability <= 0) break;

        // Calculate optimal flow rate based on availability and max rate
        const optimalRate = Math.min(connection.maxRate, remainingAvailability);

        // Update connection rate
        const updatedConnection = {
          ...connection,
          currentRate: optimalRate,
        };

        updatedConnections.push(updatedConnection);
        remainingAvailability -= optimalRate;

        // Create transfer record
        transfers.push({
          id: `transfer-${connection.id}-${Date.now()}`,
          source: connection.source,
          target: connection.target,
          resourceType: connection.resourceType,
          amount: optimalRate,
          timestamp: new Date(),
        });
      }
    }
  }

  return { updatedConnections, transfers };
}

/**
 * Calculate network efficiency based on node placement and connections
 */
function calculateNetworkEfficiency(network: {
  nodes: FlowNode[];
  connections: FlowConnection[];
}): {
  overallEfficiency: number;
  nodeEfficiencies: Record<string, number>;
  bottlenecks: string[];
} {
  const { nodes, connections } = network;
  const nodeEfficiencies: Record<string, number> = {};
  const bottlenecks: string[] = [];

  // Calculate efficiency for each node
  for (const node of nodes) {
    // Count connections to/from this node
    const nodeConnections = connections.filter(
      conn => conn.source === node.id || conn.target === node.id
    );

    // Basic efficiency calculation - more connections = higher stress = lower efficiency
    const connectionStress = Math.min(1, nodeConnections.length / 10);
    const efficiency = 1 - connectionStress;

    nodeEfficiencies[node.id] = efficiency;

    if (efficiency < 0.6) {
      bottlenecks.push(node.id);
    }
  }

  // Calculate overall network efficiency
  const overallEfficiency =
    nodes.length > 0
      ? Object.values(nodeEfficiencies).reduce((sum, val) => sum + val, 0) / nodes.length
      : 0;

  return {
    overallEfficiency,
    nodeEfficiencies,
    bottlenecks,
  };
}
